INSERT INTO `productos` VALUES ('112', '23', '1370283829-23', 'Jornada', '9na.JVLI', '9na.Jornadas Veterinarias Latinoamericanas del Interior', 'Salón Metropolitano', 'Junín 501', '2000', '64651', '80', 'Argentina', '1370574000', '1370746799', 'http://www.drovet.com/jvli/', '', 'https://www.facebook.com/drovet.insumosagroveterinarios', 'jornadas@drovet.com.ar', '', '+54 (0341) 430 10 36', '', '9na-jvli', '1', NULL, '<p>Organiza: Drovet Insumos Agroveterinarios</p>\r\n<p>Auspicia: Municipalidad de Rosario</p>', '', NULL, '', '<h3>NORMAS DE PRESENTACI&Oacute;N, ENV&Iacute;O DE&nbsp;RESUMENES, PRESENTACI&Oacute;N DE P&Oacute;STERS&nbsp;Y CASOS CL&Iacute;NICOS.</h3>\r\n<p>La participaci&oacute;n en las IX Jornadas Veterinarias Latinoamericanas del Interior podr&aacute; ser a trav&eacute;s de p&oacute;ster y comunicaci&oacute;n de casos cl&iacute;nicos. Previamente deber&aacute; enviarse un resumen por escrito.&nbsp;</p>\r\n<p><br />El Comit&eacute; Cient&iacute;fico comunicar&aacute; su decisi&oacute;n de aceptaci&oacute;n o no del resumen a la persona que conste como presentador.&nbsp;<br />Los res&uacute;menes aceptados se publicar&aacute;n en el Programa Oficial.<br />Env&iacute;o de los res&uacute;menes: hasta 15 de Mayo de 2013</p>\r\n<p><br />Env&iacute;e su presentaci&oacute;n a posters@drovet.com.ar</p>', '9na.Jornadas Veterinarias Latinoamericanas del Interior', '413', '0', '1370283829', '0', NULL, '-32.926518', '-60.67080499999997', '0', '0', '1370280597', '{\"organiza\":\"Organizaci\\u00f3n\",\"auspicios\":\"Auspicios\",\"autoridades\":\"Autoridades\",\"areas\":\"\\u00c1reas Tem\\u00e1ticas\",\"trabajos\":\"Pres.Trabajos\",\"disertantes\":\"Expositores\",\"cronograma\":\"Cronograma\",\"sponsors\":\"Sponsors\"}', NULL, NULL, '', '<h2 class=\"titulo\">7 Y 8 de Junio 2013 - Rosario - Santa Fe</h2>\r\n<h3 class=\"subtitulo\">Sal&oacute;n Metropolitano: JUN&Iacute;N 501 - Shopping Alto Rosario</h3>\r\n<p>Descargar cronogroma completo en la secci&oacute;n \'Archivos para Descargar\'</p>', '0', '0', '0', '');
INSERT INTO `productos` VALUES ('104', '14', '1366973092-14', 'Feria', 'FIAR 2013', 'Feria Internacional de la Alimentación 2013, a llevarse a cabo en la Ciudad de  Rosario, Santa Fe, Argentina', 'Metropolitano Centro de Convenciones', 'Intendente Lamas 610', '2000', '64651', '80', 'Argentina', '1367982000', '1368327599', 'http://www.fiar.com.ar/', ' ', ' ', 'contactofiar@arnetbiz.com.ar', 'contactofiar@arnetbiz.com.ar', '+54 0341 425-9111', '', 'fiar-2013', '1', NULL, '<p class=\"full\"><span>La<span class=\"apple-converted-space\">&nbsp;</span><strong>Feria Internacional de la Alimentaci&oacute;n Rosario</strong><span class=\"apple-converted-space\">&nbsp;</span>es el encuentro de empresarios del sector m&aacute;s importante de Latinoam&eacute;rica. Durante 4 d&iacute;as se exponen los productos y la m&aacute;s alta tecnolog&iacute;a vinculada a este eslab&oacute;n b&aacute;sico de la industria mundial. <br /> Es el escenario ideal para la presentaci&oacute;n de nuevos equipamientos y el espacio donde se concretan operaciones de negocios nacionales e internacionales. </span></p>\r\n<p class=\"full\"><span>Especialistas en alimentos, cient&iacute;ficos e investigadores tambi&eacute;n dan cuenta en este encuentro de las &uacute;ltimas novedades y los avances sobre diferentes aspectos que involucran este sector.</span></p>\r\n<p class=\"full\"><span>La Feria es organizada por la&nbsp;<strong>Secretar&iacute;a de Producci&oacute;n y Desarrollo Local de la Municipalidad de Rosario</strong>. Y la coordinaci&oacute;n y comercializaci&oacute;n est&aacute; a cargo de la<strong>&nbsp;Asociaci&oacute;n Empresaria de Rosario</strong>.</span></p>\r\n<p><strong><span>Contactos:</span></strong></p>\r\n<p><strong><span>&nbsp;</span></strong><span><!--[if gte vml 1]><v:shapetype id=\"_x0000_t75\"\r\n coordsize=\"21600,21600\" o:spt=\"75\" o:preferrelative=\"t\" path=\"m@4@5l@4@11@9@11@9@5xe\"\r\n filled=\"f\" stroked=\"f\">\r\n <v:stroke joinstyle=\"miter\"/>\r\n <v:formulas>\r\n  <v:f eqn=\"if lineDrawn pixelLineWidth 0\"/>\r\n  <v:f eqn=\"sum @0 1 0\"/>\r\n  <v:f eqn=\"sum 0 0 @1\"/>\r\n  <v:f eqn=\"prod @2 1 2\"/>\r\n  <v:f eqn=\"prod @3 21600 pixelWidth\"/>\r\n  <v:f eqn=\"prod @3 21600 pixelHeight\"/>\r\n  <v:f eqn=\"sum @0 0 1\"/>\r\n  <v:f eqn=\"prod @6 1 2\"/>\r\n  <v:f eqn=\"prod @7 21600 pixelWidth\"/>\r\n  <v:f eqn=\"sum @8 21600 0\"/>\r\n  <v:f eqn=\"prod @7 21600 pixelHeight\"/>\r\n  <v:f eqn=\"sum @10 21600 0\"/>\r\n </v:formulas>\r\n <v:path o:extrusionok=\"f\" gradientshapeok=\"t\" o:connecttype=\"rect\"/>\r\n <o:lock v:ext=\"edit\" aspectratio=\"t\"/>\r\n</v:shapetype><v:shape id=\"_x0000_i1026\" type=\"#_x0000_t75\" alt=\"\" style=\'width:75pt;\r\n height:75pt\'>\r\n <v:imagedata src=\"file:///H:\\DOCUME~1\\ADMINI~1\\CONFIG~1\\Temp\\msohtml1\\01\\clip_image001.jpg\"\r\n  o:href=\"http://www.rosario.gov.ar/sitio/verArchivo?id=7075&tipo=objetoMultimedia\"/>\r\n</v:shape><![endif]--><!--[if !vml]--><img src=\"http://www.rosario.gov.ar/sitio/verArchivo?id=7075&amp;tipo=objetoMultimedia\" alt=\"\" width=\"100\" height=\"100\" /><!--[endif]--></span></p>\r\n<p><strong><span>Secretar&iacute;a de Producci&oacute;n:</span></strong><span><br /> Av. Belgrano 658 | 0341-480-22-88<br /> <a href=\"mailto:fiar@rosario.gov.ar\">fiar@rosario.gov.ar</a><br /> <!--[if !supportLineBreakNewLine]--><br /> <!--[endif]--></span></p>\r\n<p><span>&nbsp;<!--[if gte vml 1]><v:shape id=\"_x0000_i1025\"\r\n type=\"#_x0000_t75\" alt=\"\" style=\'width:63.75pt;height:55.5pt\'>\r\n <v:imagedata src=\"file:///H:\\DOCUME~1\\ADMINI~1\\CONFIG~1\\Temp\\msohtml1\\01\\clip_image003.jpg\"\r\n  o:href=\"http://www.mundovivienda2012.com/img/auspiciantes/aer.jpg\"/>\r\n</v:shape><![endif]--><!--[if !vml]--><img src=\"http://www.mundovivienda2012.com/img/auspiciantes/aer.jpg\" alt=\"\" width=\"85\" height=\"74\" border=\"0\" /><!--[endif]--><br /> <strong>Asociaci&oacute;n Empresaria de Rosario:<br /> </strong>Ricardo Diab- Espa&ntilde;a 848 | Tel.:0341 425-9111&nbsp;<br /> <a href=\"mailto:contactofiar@arnetbiz.com.ar\">contactofiar@arnetbiz.com.ar</a></span></p>\r\n<div class=\"aer\">&nbsp;</div>', '<h2>&iquest;Qui&eacute;nes exponen?</h2>\r\n<p>Fabricantes, productores, importadores, representantes de equipos, procesos y tecnolog&iacute;a, insumos y servicios, y organismos oficiales.</p>\r\n<p><strong>FIAR 2013</strong>&nbsp;sum&oacute; m&aacute;s metros cuadrados y tendr&aacute; nuevos salones tem&aacute;ticos.</p>\r\n<p>La Superficie ser&aacute; de&nbsp;<strong>24.500 metros cuadrados</strong></p>\r\n<p><strong>SALONES TEM&Aacute;TICOS:</strong></p>\r\n<p><strong>ExpoAmeriCarne</strong>&nbsp;(Industria Frigor&iacute;fica)</p>\r\n<p><strong>Vinal Terra&nbsp;</strong>(Rubro Vitivin&iacute;cola)</p>\r\n<p><strong>ExpoPanificados</strong>&nbsp;(Oferta en panificaci&oacute;n)</p>\r\n<p><strong>Sal&oacute;n Gourmet</strong>&nbsp;(Espacio de Escuelas de Cocina)</p>\r\n<p><strong>Pastas Frescas</strong></p>\r\n<p><strong>Sal&oacute;n de Golosinas</strong></p>\r\n<p><strong>Sal&oacute;n de Helados</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<h2>&nbsp;REGLAMENTO</h2>\r\n<p>Fecha y lugar</p>\r\n<p>La Exposici&oacute;n se llevar&aacute; a cabo del 8 al 11 de mayo del a&ntilde;o 2013 en el predio Centro de Convenciones y Eventos METROPOLITANO ROSARIO, sito en Av. Intendente Lamas 610 &ndash; Alto Rosario Shopping- de la ciudad de Rosario, Santa Fe, Argentina.</p>\r\n<p>Horario: 8, 9 y 10 de mayo desde las 14 a 21 hs / S&aacute;bado 11 de mayo desde las 10 a 19 hs.</p>\r\n<h3>Disposiciones generales</h3>\r\n<p>Las normas del Presente Reglamento son de cumplimiento obligatorio. El Comit&eacute; Organizador (en adelante CO) est&aacute; facultado para prohibir o clausurar los stands que no se ajusten a estas disposiciones, sin que &eacute;l genere obligaci&oacute;n o indemnizatoria alguna, y / o aplicar multas por incumplimiento de los reglamentos y anexos.</p>\r\n<p>Asimismo los expositores deber&aacute;n respetar la legislaci&oacute;n nacional, ordenanzas municipales y disposiciones profesionales.</p>\r\n<p>El Centro de Exposiciones cuenta con extremas exigencias reglamentarias, siendo responsabilidad del expositor todos los deterioros que puedan producirse en el sector de stands o dentro del predio por su personal en los per&iacute;odos de armado, feria, y desarme.</p>\r\n<p>Durante todo el per&iacute;odo de construcci&oacute;n, feria, y desarme, el CO realizar&aacute; inspecciones t&eacute;cnicas y de seguridad en el predio. Los expositores deber&aacute;n dar cumplimiento a las observaciones que resultasen de dicha inspecci&oacute;n consider&aacute;ndose falta grave su desatenci&oacute;n.</p>\r\n<p><strong>1.- PEDIDO DE MODIFICACIONES Y ASESORAMIENTO</strong></p>\r\n<p>El Departamento T&eacute;cnico asesorar&aacute; gratuitamente al expositor con referencia a los aspectos t&eacute;cnicos de la muestra e interpretaci&oacute;n del presente reglamento y anexos.</p>\r\n<p>Consultas:</p>\r\n<p>Email:&nbsp;<a href=\"mailto:contactofiar@arnetbiz.com.ar\">contactofiar@arnetbiz.com.ar</a></p>\r\n<p>Tel. 54-341-4259111</p>\r\n<p>Rosario &ndash; Santa Fe - Argentina&nbsp;</p>\r\n<p><strong><span>2- PRESENTACI&Oacute;N DE PLANOS</span></strong></p>\r\n<p>Los expositores deber&aacute;n presentar antes del 26 de abril de 2013 (Espa&ntilde;a 848 de la ciudad de Rosario, Santa Fe, Argentina. Tel 0341- 425 9111) 2 (dos) copias de planos (planta y vista) en escala 1:100 o 1:50, y una memoria constructiva, ubicaci&oacute;n de equipamiento y m&aacute;quinas pesadas (m&aacute;s de 150 kg x m2).</p>\r\n<p>SE DEBERA CONTAR CON AUTORIZACI&Oacute;N EXPRESA DEL CO</p>\r\n<p>Deber&aacute; tambi&eacute;n figurar claramente el nombre de la empresa expositora, n&uacute;mero de lote, y el nombre, domicilio, email, y tel&eacute;fono del constructor del stand.<strong>&nbsp;</strong></p>\r\n<p><strong>3 &ndash; PERMISO DE CONSTRUCCI&Oacute;N</strong></p>\r\n<p>El CO entregar&aacute; un permiso de construcci&oacute;n a las empresas arrendatarias, luego del cumplimiento de sus obligaciones y previo al inicio de la construcci&oacute;n del stand.</p>\r\n<p><a id=\"04\" name=\"04\"></a><strong>&nbsp;</strong><strong>4 &ndash; PLAZO DE EJECUCI&Oacute;N</strong></p>\r\n<p>ARMADO: La construcci&oacute;n y ornamentaci&oacute;n del stand debe hacerse en el horario de 9 a 20 hs, desde el d&iacute;a 3 de mayo del 2013. El d&iacute;a 7 de mayo a partir de las 14 a 18 hs ser&aacute; exclusivamente para el ingreso de mercader&iacute;a y limpieza de stands.</p>\r\n<p>DESARME: Los expositores est&aacute;n obligados a retirar sus productos, desarmar y retirar los elementos constitutivos del stand y a devolver el espacio y los elementos provistos por la muestra en iguales condiciones a la de su recepci&oacute;n los d&iacute;as 11 (objetos manuales de 19 a 21 hs / el expositor deber&aacute; contar con la autorizaci&oacute;n previa de la CO), 13 y 14 de mayo (desde las 9 hs a 17 hs). Los productos e instalaciones que a&uacute;n permanezcan en el stand despu&eacute;s de dicha fecha ser&aacute;n retirados por el CO y no se reconocer&aacute;n reclamos de ninguna naturaleza.</p>\r\n<p>A los efectos de operar en la carga y descarga de materiales se determina un tiempo de: 30 minutos para autom&oacute;viles; 1:30 hs para camionetas; y 2 horas para camiones.</p>\r\n<p>Con vistas al inter&eacute;s general, se solicita a los expositores que se adecuen a las indicaciones proporcionadas anteriormente para acceder al predio.</p>\r\n<p>El CO colocar&aacute; personal para ayudar con la circulaci&oacute;n y el estacionamiento.</p>\r\n<p>EL ESPACIO EN LAS INMEDIACIONES DEL PREDIO ES REDUCIDO, por lo tanto se solicita a los expositores limitar el&nbsp;<strong>tiempo de estacionamiento &uacute;nicamente para la carga y descarga del material</strong>, liberando el espacio con el objeto de facilitar el acceso.&nbsp;</p>\r\n<p><a id=\"05\" name=\"05\"></a><strong>&nbsp;</strong><strong>5 - SEGURO</strong></p>\r\n<p>1 - El expositor ser&aacute; el &uacute;nico responsable frente a terceros y personal propio, civil, penal, y administrativamente por las consecuencias que se deriven del accionar de su personal, del uso indebido de la superficie que se le concede, y del material expuesto; incluyendo accidentes de trabajo, da&ntilde;os y perjuicios por intoxicaci&oacute;n, incendios, siniestros, robos, tempestades, explosivos, granizo, suministros de alimentos, multitud o cualquier otra circunstancia.</p>\r\n<p>2 &ndash; A tal efecto el expositor est&aacute; obligado a contratar un seguro contra terceros y todo riesgo a su cargo, desde el 3 de mayo (comienzo de armado de su stand) y hasta el d&iacute;a 14 de mayo inclusive (&uacute;ltimo d&iacute;a del desarme)</p>\r\n<p>3 &ndash; El personal que realiza tareas de cualquier &iacute;ndole a cargo del expositor, deber&aacute; encontrarse cubierto por una p&oacute;liza de seguro a cargo del expositor directo, por los riesgos mencionados en la ley 24.028 de accidentes de trabajo y sus modificaciones.</p>\r\n<p>4 &ndash; El CO bajo ning&uacute;n concepto ser&aacute; responsable por da&ntilde;os o perjuicios emergentes del p&uacute;blico o de terceros, ni por p&eacute;rdidas de ninguna &iacute;ndole.</p>\r\n<p>Disposiciones para el proyecto y construcci&oacute;n de stands</p>\r\n<p><strong>1 &ndash; ELEMENTOS PROVISTOS POR LA EXPOSICI&Oacute;N</strong></p>\r\n<p>ALFOMBRA<br /> PANELERIA DIVISORIA</p>\r\n<p>CONEXI&Oacute;N ELECTRICA</p>\r\n<p>CENEFA</p>\r\n<p><strong>1. 1</strong>&nbsp;&ndash; ALFOMBRA</p>\r\n<p>Color existente</p>\r\n<p><strong>&nbsp;</strong><strong>1. 2</strong>&nbsp;- PANELERIA DIVISORIA</p>\r\n<p>Materializada por paneles de chapadur plus blanco o similar altura de 2,40 metros (estas medidas se deben verificar para cada pabell&oacute;n en particular) con estructura de aluminio. Sobre estos se pueden fijar solamente materiales autoadhesivos pero no pintar ni fijar elementos con pegamentos de ninguna naturaleza. La estructura no debe utilizarse para soporte de vitrinas, cielorrasos u otros elementos, s&oacute;lo para murales o muestrarios que no superen los 5 kg en cada panel, los que se colgar&aacute;n de la barra superior.</p>\r\n<p>No se puede agujerear, atornillar, ni clavar con elementos de ning&uacute;n tama&ntilde;o. Los paneles da&ntilde;ados se facturar&aacute;n al expositor.</p>\r\n<p><strong>1. 3</strong>&nbsp;&ndash; CONEXI&Oacute;N Y ENERGIA ELECTRICA</p>\r\n<p>A) La conexi&oacute;n el&eacute;ctrica de los stans la realizar&aacute; exclusivamente el personal autorizado por CO<br /> B) El consumo requerido por el expositor debe ser solicitado mediante la presentaci&oacute;n de la solicitud de consumo el&eacute;ctrico.</p>\r\n<p>C) Un spot</p>\r\n<p><strong>&nbsp;1</strong><strong>. 4&nbsp;</strong>&ndash; CENEFA</p>\r\n<p>Cenefa de frente h: 32 cm (h: libre panel: 22 cm) compuesta por perfiles de aluminio anodinado y paneles de laminado pl&aacute;stico blanco.&nbsp;</p>\r\n<p><a id=\"07\" name=\"07\"></a><strong>&nbsp;</strong><strong>2 - INSTALACIONES EXISTENTES</strong></p>\r\n<p><strong>2. 1</strong>&nbsp;&ndash; Est&aacute; prohibido perforar, romper, deteriorar o excavar canales en el pavimento. Asimismo queda prohibido picar, agujerear, soldar, cortar, pintar y todo aquello que pueda deteriorar las instalaciones existentes. Es exclusiva responsabilidad del expositor devolver el espacio arrendado en las mismas condiciones en que le fuera entregado.</p>\r\n<p>Previo a su devoluci&oacute;n, el espacio ser&aacute; inspeccionado por el personal del CO, entreg&aacute;ndose un recibo de conformidad en caso de no mediar observaciones.</p>\r\n<p>En caso de comprobarse deterioros, su reparaci&oacute;n ser&aacute; facturada al expositor, sin posibilidad de apelaci&oacute;n alguna.</p>\r\n<p><strong>&nbsp;</strong><strong>2. 2</strong>&nbsp;&ndash; Los tableros de electricidad general y las llaves de paso de agua ubicadas en posiciones que resultan dentro de los lotes deber&aacute;n mantener f&aacute;cil acceso permanentemente.</p>\r\n<p><strong>2. 3</strong>&nbsp;- Los elementos de prevenci&oacute;n de incendios deber&aacute;n quedar visibles y con f&aacute;cil acceso, cumpliendo con las normas municipales de seguridad, no pudiendo colocar objetos y / o muebles a una distancia inferior a 1 metro (un metro).</p>\r\n<p><strong>2. 4&nbsp;</strong>- No se admiten fundiciones ni cimientos.</p>\r\n<p><strong>2. 5</strong>&nbsp;- Todas las construcciones deber&aacute;n apoyarse sobre el piso existente, sin romperlo.</p>\r\n<p><a id=\"08\" name=\"08\"></a><strong>&nbsp;</strong></p>\r\n<p><strong>3 &ndash; MATERIALES</strong></p>\r\n<p><strong>3.1</strong>&nbsp;&ndash; El Cap&iacute;tulo 18 de la ley 19 587 hace referencia a la clasificaci&oacute;n de los materiales en funci&oacute;n de su comportamiento frente al calor u otra forma de energ&iacute;a, dividi&eacute;ndolos en:<br /> 1. Explosivos</p>\r\n<p>2. Inflamables de 1&ordf; categor&iacute;a</p>\r\n<p>3. Inflamables de 2&ordf; categor&iacute;a</p>\r\n<p>4. Muy combustibles</p>\r\n<p>5. Combustibles</p>\r\n<p>6. Poco Combustibles</p>\r\n<p>7. Incombustibles</p>\r\n<p>8. Refractarios</p>\r\n<p>Quedan terminantemente prohibidos los materiales del tipo 1, 2, y 3.</p>\r\n<p>Los materiales del grado 4 y 5 deber&aacute;n ser tratados con retardadores.</p>\r\n<p>No existen restricciones para los materiales del tipo 6, 7, y 8.</p>\r\n<p><strong>3.2&nbsp;</strong>Est&aacute; prohibida la utilizaci&oacute;n de pistolas, como as&iacute; tambi&eacute;n pinturas de laca, o celuloides para pintar en el interior de los pabellones. Esta prohibici&oacute;n se hace extensiva a l&iacute;quidos inflamables y al empleo de aerosoles con gas butano.</p>\r\n<p>Durante los trabajos de soldadura, deber&aacute; solicitarse el permiso correspondiente, estar en el contorno suficientemente protegido y disponer el extintor, propiedad del solicitante, para evitar posibles incendios.</p>\r\n<p><strong>3.3</strong>&nbsp;Est&aacute;n prohibidas las construcciones h&uacute;medas. Por ejemplo: ladrillos, cementos, mezclas, yesos, etc.</p>\r\n<p><strong>4 &ndash; ALTURA DE LAS CONSTRUCCIONES</strong></p>\r\n<p><strong>4.1</strong>&nbsp;La altura m&aacute;xima de las construcciones es de 2.4 cm en el primer metro perimetral, pudi&eacute;ndose superar dicha altura en un metro por cada metro de retiro (hasta un m&aacute;ximo de 4 metros). Ej: para construcciones de 3,50 metros de altura deber&aacute; retirarse un metro del l&iacute;mite del loto. Todo elemento, como carteles, torres de iluminaci&oacute;n, etc. Deber&aacute;n cumplir esta disposici&oacute;n.</p>\r\n<p>VERIFICAR ESTAS MEDIDAS PARA CADA PABELL&Oacute;N DEBIDO A QUE HAY CARPAS DE DISTINTAS ALTURAS.</p>\r\n<p><strong>4.2</strong>&nbsp;Todo elemento que exceda los 2,40 metros de altura deber&aacute; tener todas sus caras visibles tratadas con la misma jerarqu&iacute;a.</p>\r\n<p><strong>4.3</strong>&nbsp;Para construcciones de altura superior a 2,50 metros es responsabilidad del expositor verificar la altura libre existente en su emplazamiento.</p>\r\n<p><strong>4.4</strong>&nbsp;Los stands con entrepisos accesibles deber&aacute;n contemplar no s&oacute;lo las normas enunciadas precedentemente, sino tambi&eacute;n las normas particulares de dichos casos.</p>\r\n<p>CONSULTAR AL DEPARTAMENTO DE SERVICIOS</p>\r\n<p>CUALQUIER DUDA O INQUIETUD</p>\r\n<p>Tel: 54 &ndash; 341 &ndash; 425 9111</p>\r\n<p>Email:&nbsp;<a href=\"mailto:contactofiar@arnetbiz.com.ar\">contactofiar@arnetbiz.com.ar</a>&nbsp;</p>\r\n<p><strong>5 - FRENTE DE LOS STANDS</strong><a id=\"10\" name=\"10\"></a></p>\r\n<p><strong>5.1</strong>&nbsp;Los frentes de los stands pueden cerrarse exclusivamente con:<br /> a) Exhibidores de productos (vitrinas, mesas) y cerramientos de recintos privados, los que deber&aacute;n ser transparente entre 1 mt y 2,20 mt hasta el 70% del largo del frente.<br /> b) Con cenefas a partir de los 2, 20 mt de altura</p>\r\n<p><strong>5.2</strong>&nbsp;En los frentes no se permiten carteles promoci&oacute;nales. Solamente el logotipo y el nombre del expositor.</p>\r\n<p><strong>5.3</strong>&nbsp;Todas las fachadas o laterales que den a un pasillo deben tener una abertura de 3 mt cada 6 m lineales.</p>\r\n<p><strong>5.4</strong>&nbsp;Para los stands de m&aacute;s de 150 mt2 se deber&aacute; someter obligatoriamente a la aprobaci&oacute;n de la organizaci&oacute;n de un proyecto de distribuci&oacute;n y exhibici&oacute;n de los materiales.</p>\r\n<p><strong>6 - ELECTRICIDAD</strong></p>\r\n<p><strong>6.1</strong>&nbsp;Queda expresamente prohibido efectuar conexiones clandestinas y / o conectar potencias superiores a las solicitadas.</p>\r\n<p><strong>6.2</strong>&nbsp;Toda instalaci&oacute;n el&eacute;ctrica que ejecuten o hagan ejecutar terceros en el predio debe cumplir obligatoriamente con la REGLAMENTACI&Oacute;N PARA LA EJECUCI&Oacute;N DE INSTALACIONES ELECTRICAS EN INMUEBLES de la Asociaci&oacute;n Electrot&eacute;cnica Argentina (AEA) set 96 o lo que lo reemplace, as&iacute; como lo indicado en los documentos PRACTICA CONFORME emitidos por la misma Asociaci&oacute;n.</p>\r\n<p>Las instalaciones el&eacute;ctricas deber&aacute;n cumplir las siguientes exigencias m&iacute;nimas:</p>\r\n<p>- llave t&eacute;rmica</p>\r\n<p>- protectores diferenciales (disyuntores) para el total de la carga del stand.</p>\r\n<p>- Cable a tierra</p>\r\n<p>Una vez ejecutada la instalaci&oacute;n el&eacute;ctrica se presentar&aacute; por triplicado una copia del certificado de conformidad otorgado por el electricista a cargo del pabell&oacute;n.</p>\r\n<p>Dicha documentaci&oacute;n se entregar&aacute; en la oficina servicios: Espa&ntilde;a 848. Rosario (Santa Fe &ndash; Argentina)</p>\r\n<p>La presentaci&oacute;n de los certificados completos y firmados es requisito previo a la apertura de la Feria al p&uacute;blico.</p>\r\n<p><strong>6.3</strong>&nbsp;6.3 De requerir energ&iacute;a constante para equipos de fr&iacute;o u otros deber&aacute; estar expresado en la SOLICITUD de CONSUMO EL&Eacute;CTRICO de presentaci&oacute;n OBLIGATORIA.</p>\r\n<p><strong>6.4</strong>&nbsp;La presentaci&oacute;n de dicha solicitud vence el d&iacute;a 26 de ABRIL de 2013. La unidad de medida para esta solicitud ser&aacute; el KW (equivalente a 1000 wats). El consumo el&eacute;ctrico solicitado no se podr&aacute; fraccionar y ser&aacute; verificado por el personal del CO durante el per&iacute;odo de duraci&oacute;n de la FERIA.</p>\r\n<p><strong>6.5</strong>&nbsp;Los pedidos recibidos luego de la fecha l&iacute;mite de presentaci&oacute;n de la solicitud, ser&aacute;n condicionados a la disponibilidad t&eacute;cnica existente en el predio.&nbsp;</p>\r\n<p><strong>7 &ndash; NORMAS DE SEGURIDAD</strong></p>\r\n<p><strong>7.1</strong>&nbsp;No se permite el empleo de globos llenos de gas inflamable en el interior de los pabellones.</p>\r\n<p><strong>7.2</strong>&nbsp;En el &aacute;mbito de la exposici&oacute;n s&oacute;lo se podr&aacute; fumar en los lugares habilitados para tales efectos. Esta norma no s&oacute;lo rige en el transcurso de la exposici&oacute;n sino tambi&eacute;n en los per&iacute;odos del armado y desarme. Se ruega a los Expositores DAR A CONOCER estas normas a TODO EL PERSONAL QUE TRABAJE PARA &Eacute;L.</p>\r\n<p><strong>7.3</strong>&nbsp;En los stands no deber&aacute;n tenerse recipientes con material inflamable. Los papeleros y cubos de basura deber&aacute;n vaciarse a la hora del cierre depositando el contenido en los recipientes colectores previstos a tal efecto por el CO.</p>\r\n<p><strong>7.4</strong>&nbsp;Los cantos de los cristaleros deber&aacute;n estar protegidos o pulidos de manera que no exista peligro de corte. Las puertas de vidrio o cualquier otra construcci&oacute;n completa de vidrio deber&aacute;n estar se&ntilde;alizadas a la altura de la vista.</p>\r\n<p><strong>7.5</strong>&nbsp;Los bordes de vidrio org&aacute;nico deber&aacute;n protegerse en forma adecuada para evitar que el fuego prenda en ellos.</p>\r\n<p><strong>7.6</strong>&nbsp;Durante el montaje y desarme no podr&aacute;n depositarse en los pasillos ning&uacute;n tipo de material o herramientas de trabajo que puedan obstruir el paso por ellos.</p>\r\n<p><strong>7.7</strong>&nbsp;Bajo ning&uacute;n concepto podr&aacute;n taparse las v&iacute;as de evacuaci&oacute;n (salidas de emergencia), la se&ntilde;alizaci&oacute;n del predio, los extintores, las bocas de incendio, los indicadores, y dem&aacute;s equipos de protecci&oacute;n.</p>\r\n<p><strong>7.8</strong>&nbsp;En los despachos, dep&oacute;sitos, o cerramientos decorativos de columnas, no se permitir&aacute;n almacenar productos inflamables.</p>\r\n<p><strong>7.9</strong>&nbsp;Asimismo el paso a las cajas de conexiones y accesos a equipos o dependencias de los pabellones no podr&aacute;n ser cerrados con llaves.</p>\r\n<p><strong>7.10</strong>&nbsp;7.10 Los materiales como telas de decoraci&oacute;n, colas de impacto, maquetas, etc. Deben ser ign&iacute;fugos debi&eacute;ndose presentar los correspondientes certificados. &nbsp;</p>\r\n<p><strong>8 &ndash; MATAFUEGOS</strong></p>\r\n<p>Es obligatorio, de acuerdo con las disposiciones vigentes del Departamento de Bomberos de la Polic&iacute;a Federal, disponer en cada stand (cada 50 m2) de superficies o fracci&oacute;n) un matafuego de 10 kg de carga m&iacute;nima del tipo ABC.</p>\r\n<p>El mismo debe ubicarse en un lugar visible y de f&aacute;cil acceso.</p>\r\n<p>El expositor debe verificar que siempre haya en su stand una persona que sepa utilizar el matafuego.</p>\r\n<p><strong>9 &ndash; CIELORRASOS</strong></p>\r\n<p><strong>9.1</strong>&nbsp;En todos los casos deber&aacute;n ser autoportantes; es decir no pueden apoyarse sobre la estructura de los pabellones y carpas.</p>\r\n<p><strong>9.2</strong> Los materiales deben ser ign&iacute;fugos y permeables en un m&iacute;nimo del 50% de su superficie de forma uniforme. Ejemplo: enrejillados met&aacute;licos; chapas perforadas)</p>\r\n<p><strong>9.3</strong>&nbsp;Quedan prohibidos los cielorrasos en los segundos niveles de construcci&oacute;n&nbsp;</p>\r\n<p><strong>10 - COLGANTES</strong><a id=\"15\" name=\"15\"></a></p>\r\n<p>Est&aacute; prohibido colgar y arriostrar cualquier elemento a la estructura de los pabellones. Salvo expreso consentimiento por el CO.</p>\r\n<p><strong>11 - CARTELES</strong></p>\r\n<p><strong>11.1</strong>&nbsp;Los mismos deben cumplir con las restricciones de altura de construcciones.</p>\r\n<p><strong>11.2</strong> Est&aacute;n prohibidos con luces destellantes e intermitentes.</p>\r\n<p><strong>11.3</strong>&nbsp;Los materiales con ne&oacute;n y que est&eacute;n expuestos a las alturas menores a 2,50 mt deber&aacute;n contar con protecci&oacute;n frontal.</p>\r\n<p><strong>12 - ILUMINACI&Oacute;N</strong></p>\r\n<p>En la instalaci&oacute;n de los artefactos debe cuidarse que los mismos no causen molestias al p&uacute;blico, ni a los stands circundantes.</p>\r\n<p><strong>13 &ndash; SONIDO</strong></p>\r\n<p>Deber&aacute; instalarse en condiciones que no superen los l&iacute;mites del stand; no pudiendo el nivel sonoro audible, fuera del &aacute;rea del mismo, exceder los 20 decibeles.</p>\r\n<p>El CO se reserva el derecho de limitar el nivel de sonido si lo considerase excesivo.</p>\r\n<p>Cada expositor se har&aacute; responsable por los pagos de los impuestos (SADAIC-ADICAPIF-ARGENTORES o cualquier otra entidad con derecho suficiente) que genere el sonido de su stand.</p>\r\n<p><strong>14 &ndash; ENTREPISO</strong></p>\r\n<p>* El Expositor que desee realizar un entrepiso deber&aacute; pedir por anticipado dicha autorizaci&oacute;n y el anexo con las normas especiales para la realizaci&oacute;n de los mismos, no pudiendo estos exceder el 25% de la superficie en planta de su stand.</p>\r\n<p>* El per&iacute;metro de los entrepisos deber&aacute; estar retirado de los per&iacute;metros del lote no menos de 1.50 mt.</p>\r\n<p>* Los entrepisos deber&aacute;n contar con todas las normas de seguridad, las que ser&aacute;n verificadas por el CO</p>\r\n<p>* Las barandas deber&aacute;n tener un m&iacute;nimo de 0,90 mt de altura.</p>\r\n<p>* No est&aacute;n permitidos los cielorrasos en los segundos niveles de construcci&oacute;n.</p>\r\n<p>* La altura m&aacute;xima de un entrepiso no podr&aacute; superar los 3 metros y las gr&aacute;ficas o cenefas situadas en ese nivel no podr&aacute;n exceder la altura m&aacute;xima permitida de construcci&oacute;n</p>\r\n<p><strong>15 - VARIOS</strong></p>\r\n<p><strong>15.1</strong>&nbsp;DEGUSTACIONES<br /> a- Los mostradores destinados a tales fines deber&aacute;n estar retirados un m&iacute;nimo de 1 mt del pasillo.</p>\r\n<p>b- Cercanos a los anteriores es obligatorio la colocaci&oacute;n de recipientes de residuos.</p>\r\n<p>c- No se permite la utilizaci&oacute;n de gas envasado ni combustibles l&iacute;quidos; debiendo ser reemplazados &eacute;stos por equipamiento el&eacute;ctrico.</p>\r\n<p>d- Si la cocci&oacute;n produjera humo o fuertes aromas, deber&aacute;n colocarse extractores o purificadores de aire.</p>\r\n<p><strong>15. 2</strong>&nbsp;LIMPIEZA</p>\r\n<p>a- FIAR 2013 hace la limpieza de las &aacute;reas comunes.</p>\r\n<p>b- La limpieza del stand es responsabilidad del expositor, quien est&aacute; obligado a mantenerlo en perfectas condiciones de higiene.</p>\r\n<p>c- En caso de ser necesario el CO podr&aacute; disponer efectuar la limpieza del stand, siendo esta facturada con cargo al expositor.</p>\r\n<p><strong>15.3</strong>&nbsp;VIGILANCIA y SEGURIDAD</p>\r\n<p>a- El CO establecer&aacute; personal de vigilancia general para resguardar el orden.</p>\r\n<p>b- En el caso que el expositor desee contratar vigilancia exclusiva, no siendo &eacute;sta la prestadora oficial, deber&aacute; notificar al CO los datos de este personal (Datos de las personas y de la empresa contratada)</p>\r\n<p>c- Durante las horas de cierre al p&uacute;blico no podr&aacute; permanecer dentro del recito de la Feria otro personal que el de la vigilancia citado.</p>\r\n<p><strong>15.4</strong>&nbsp;PROVICION DE AGUA</p>\r\n<p>a- El Expositor podr&aacute; pedir provisi&oacute;n de agua mediante la presentaci&oacute;n de la SOLICITUD DE PROVISI&Oacute;N DE AGUA.</p>\r\n<p>b- La conexi&oacute;n de agua es con CARGO ADICIONAL. El costo de este servicio deber&aacute; ser consultado al CO.</p>\r\n<p>c- Las mismas consisten en una boca de &frac34; pulgadas con llave de paso para agua.</p>\r\n<p><strong>15.5</strong>&nbsp;SERVICIOS TELEFONICOS</p>\r\n<p>a- El expositor podr&aacute; pedir tel&eacute;fono mediante la presentaci&oacute;n de la SOLICITUD DE SERVICIO TELEFONICO.</p>\r\n<p>b- La conexi&oacute;n de tel&eacute;fono es con CARGO ADICIONAL y deber&aacute; consultarse en el CO</p>\r\n<p><strong>15.6</strong>&nbsp;FIAR 2013 dispondr&aacute; en el predio de una empresa prestataria de servicios de movimiento de cargas (autoelevadores) servicio que estar&aacute; disponible y a cargo de los expositores.</p>\r\n<p>Para mayor informaci&oacute;n consultar al AREA DE SERVICIOS:</p>\r\n<p>Tel: 54- 341- 425 9111</p>\r\n<p>Email:&nbsp;<a href=\"mailto:contactofiar@arnetbiz.com.ar\">contactofiar@arnetbiz.com.ar</a></p>\r\n<p>Direcci&oacute;n: Espa&ntilde;a 848</p>\r\n<p>Rosario &ndash; Santa Fe - Argentina</p>', NULL, '<p><span class=\"bold\">Si sos expositor</span><br />Consultas: Ricardo Diab<br />Asociaci&oacute;n Empresaria de Rosario<br />Espa&ntilde;a 848 | Tel.: 0341 425-9111<br /><a href=\"mailto:contactofiar@arnetbiz.com.ar\">contactofiar@arnetbiz.com.ar </a></p>\r\n<p><br /> <span class=\"bold\">Si sos visitante</span><br />-Directivos y ejecutivos de empresas.<br />-Representantes de instituciones p&uacute;blicas y privadas.<br />-Operadores de la distribuci&oacute;n y el comercio.<br />-Autoridades nacionales e internacionales vinculadas al sector.<br />-Estudiantes secundarios avanzados mayores de 16 a&ntilde;os(*)<br />-Estudiantes terciarios, universitarios y profesionales de carreras vinculadas al sector (*)<br />(*) Consultar por visitas educativas de contingentes a fiar@rosario.gov.ar</p>', '', 'feria internacional de la alimentacion, fiar 2013, fiar rosario, ', '463', '0', '1366973092', '0', NULL, '-32.92663653885782', '-60.66692388902436', '0', '0', '1366898768', '{\"organiza\":\"Organizaci\\u00f3n\",\"autoridades\":\"Autoridades\",\"areas\":\"Expositores\",\"trabajos\":\"Pres.Trabajos\",\"disertantes\":\"Expositores\",\"cronograma\":\"Actividades\",\"auspicios\":\"Auspicios\"}', NULL, NULL, NULL, '<h2>Ronda de negocios</h2>\r\n<p><strong>FIAR</strong>&nbsp;es una ventana al mundo y la ronda de negocios es una excelente oportunidad para vincularse con potenciales clientes y proveedores de manera simult&aacute;nea.</p>\r\n<p>Esta instancia posibilita el intercambio comercial y genera nuevos canales de comunicaci&oacute;n.</p>\r\n<p><strong>Informes e inscripci&oacute;n:</strong>&nbsp;<a href=\"mailto:producci%C3%B3n@rosario.gov.ar\"><strong>producci&oacute;n@rosario.gov.ar</strong></a>&nbsp;/ (0341) 4802288&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h2>Foro Internacional sobre la Industria del Alimento - Fiar 2013</h2>\r\n<p><strong>ROSARIO: CIENCIA, EMPRESAS Y SEGURIDAD ALIMENTARIA</strong></p>\r\n<table style=\"width: 100%;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td colspan=\"2\">\r\n<p><strong>Jueves 9 de mayo de 2013</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\">\r\n<p>Por la ma&ntilde;ana:&nbsp;<strong>EnTec</strong>&nbsp;(Encuentro Tecnol&oacute;gico sobre Alimentos)</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\">\r\n<p><strong>Tarde</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p>15 hs.</p>\r\n</td>\r\n<td>\r\n<p><strong>Acreditaci&oacute;n</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"100\">\r\n<p>15,15 / 15.45</p>\r\n</td>\r\n<td>\r\n<p><strong>APERTURA DEL FORO:</strong></p>\r\n<p><strong>Dra. M&oacute;nica Fein</strong><strong>&nbsp;</strong>&ndash; Intendenta de la ciudad de Rosario<br /><strong>Dr. Marcos Monteverde</strong>&nbsp;&ndash; Secretario de la ASSAL (Agencia Santafesina de Seguridad Alimentaria &ndash; Ministerio de Salud - Provincia de Santa Fe)<br /><strong>Lic. Eleonora Scagliotti</strong>&nbsp;&ndash; Secretaria de Producci&oacute;n y Desarrollo Local (MR)<br /><strong>Dr. Leonardo Caruana</strong>&nbsp;&ndash; Secretario de Salud P&uacute;blica (MR)</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">\r\n<p>15.45 / 16.05</p>\r\n</td>\r\n<td>\r\n<p><strong>CIENCIA TECNOLOG&Iacute;A E INNOVACI&Oacute;N EN ROSARIO Y LA REGI&Oacute;N<br /></strong><strong>Mg. Mart&iacute;n Guinart</strong>&nbsp;- Director Nacional de Desarrollo Tecnol&oacute;gico e Innovaci&oacute;n Productiva - Ministerio de Ciencia, Tecnolog&iacute;a e Innovaci&oacute;n Productiva de la Naci&oacute;n</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">\r\n<p>16.05 / 17.00</p>\r\n</td>\r\n<td>\r\n<p><strong>MOLECULAR FARMING: BIOMOL&Eacute;CULAS INDUSTRIALES A PARTIR DE PLANTAS</strong><br /> Importantes avances de la biotecnolog&iacute;a local en la producci&oacute;n industrial de queso: Quimosina a partir de granos de c&aacute;rtamo.<br /> INDEAR, con su planta piloto de producci&oacute;n, es pionera en el desarrollo de tecnolog&iacute;as que permiten utilizar plantas como bioreactores para producir enzimas de valor para la industria de los alimentos.<br /><br /><strong>Dr. Mart&iacute;n A. Salinas</strong><strong>&nbsp;</strong>&ndash; Dr. en Ciencias Biol&oacute;gicas e Ing. Qu&iacute;m.(Univ. Nac. de Tucum&aacute;n). Gerente de Planta de INDEAR.<br />Ha desarrollado actividad docente y de investigaci&oacute;n en Tucum&aacute;n, Alemania y en Conicet y luego se ha dedicado a la actividad industrial privada.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p>17.00 / 17.30</p>\r\n</td>\r\n<td>\r\n<p><strong>Receso</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">\r\n<p>17,30 / 19.00</p>\r\n</td>\r\n<td>\r\n<p><strong>EL DESARROLLO DE LA SEGURIDAD ALIMENTARIA EN ROSARIO.<br /></strong><strong>- Agricultura urbana:<br /></strong><strong>Ing. Agr. Antonio Lattuca</strong>&nbsp;(Director del Programa de Agricultura Urbana &ndash; Secretar&iacute;a de Promoci&oacute;n Social (MR)<br /><strong>- Seguridad alimentaria en producci&oacute;n frutihort&iacute;cola periurbana:<br /></strong><strong>Med. Vet. Dr. Roberto Llanes</strong>&nbsp;(Coordinador del Programa Alimentario de la Secretar&iacute;a de Producci&oacute;n y Desarrollo Local (MR)<br /><strong>- Instituto del Alimento: Sus aportes y l&iacute;neas de acci&oacute;n.<br /></strong><strong>Ing. Qu&iacute;m. Susana Due&ntilde;as</strong>&nbsp;&ndash; Directora del Instituto del Alimento (MR)</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style=\"width: 100%;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td colspan=\"2\">\r\n<p><strong>Viernes 10 de mayo de 2013</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\">\r\n<p><strong>Ma&ntilde;ana</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">\r\n<p>10,30 / 12</p>\r\n</td>\r\n<td>\r\n<p><strong>LA PROBLEM&Aacute;TICA DEL FR&Iacute;O</strong>. Los requerimientos t&eacute;cnicos. El aporte de las empresas locales.<br /><strong>Sr. Ra&uacute;l G&oacute;mez</strong>&nbsp;&ndash; Especialista en Fr&iacute;o de Frigofe S.A.<br /><strong>Ing. Alejandro Vittone</strong>&nbsp;&ndash; Director t&eacute;cnico industrial de Arneg Argentina S.A.<br />Coordinaci&oacute;n y presentaci&oacute;n:&nbsp;<strong>Lic. Jorge Baigorri</strong>&nbsp;(Instituto del Alimento &ndash; MR)</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\">\r\n<p><strong>Tarde</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">\r\n<p>15.00 / 16.00</p>\r\n</td>\r\n<td>\r\n<p><strong>LA INDUSTRIA DE LA ALIMENTACI&Oacute;N EN ROSARIO Y SUS PRINCIPALES CLUSTERS<br /></strong><strong>Lic. Eleonora Scagliotti</strong>, Secretaria de Producci&oacute;n y Desarrollo Local de la Municipalidad de Rosario.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"100\">\r\n<p>16.00 / 16.30</p>\r\n</td>\r\n<td>\r\n<p><strong>ACTUALIZACION EN SEGURIDAD ALIMENTARIA EN LA INDUSTRIA CARNICA<br /></strong><strong>Med. Vet. Rub&eacute;n Sartori</strong>&nbsp;- Supervisor Sur de la Coordinaci&oacute;n de Inocuidad y Calidad Agroalimentaria del Centro Regional Santa Fe del Senasa.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">\r\n<p>16.30 / 17.15</p>\r\n</td>\r\n<td>\r\n<p><strong>NUEVOS PROYECTOS EN DESARROLLO EN ROSARIO:<br /></strong><strong>- Buenas Pr&aacute;cticas Ambientales en la Industria Frigor&iacute;fica. CIMPAR<br /></strong><strong>Ing. Daniela Mastr&aacute;ngelo</strong>&nbsp;&ndash; Presidenta de CIMPAR<br />(Comisi&oacute;n Interempresaria Municipal de Protecci&oacute;n Ambiental Regi&oacute;n Rosario)</p>\r\n<p>-&nbsp;<strong>Presentaci&oacute;n de la nueva carrera: &ldquo;Licenciatura en ciencia y tecnolog&iacute;a de los alimentos&rdquo;</strong><strong>&nbsp;</strong>de la Facultad de Ciencias Bioqu&iacute;micas y Farmac&eacute;uticas de la U.N.R.&nbsp;<br /><strong>Decano Dr. Esteban Serra</strong>&nbsp;/ Director de la Carrera:&nbsp;<strong>Dr. N&eacute;stor Delorenzi</strong></p>\r\n<p><strong>- El rol de los Bancos de Alimentos en la cadena alimentaria:<br /></strong><strong>Luciano Schiaffino</strong>. Secretario de la Asociaci&oacute;n Civil BAR (Banco de Alimentos Rosario).<br />La Asociaci&oacute;n Civil BAR recibe, clasifica y almacena los productos donados conforme a las normas sanitarias en vigencia, para luego distribuirlos entre las instituciones comunitarias previamente incorporadas como entidades beneficiarias.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">\r\n<p>17.15 / 17.45</p>\r\n</td>\r\n<td>\r\n<p><strong>Receso</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\">\r\n<p>17.45 / 19.15</p>\r\n</td>\r\n<td>\r\n<p><strong>NANOTECNOLOG&Iacute;AS EN EL SECTOR ALIMENTARIO</strong></p>\r\n<p><strong>- Nanomateriales: Su aplicaci&oacute;n en industrias vinculadas a los alimentos&rdquo;<br /></strong><strong>Dra. Silvia Goyanes</strong>&nbsp;&ndash; Lic. y Dr. en Ciencias F&iacute;sicas (UBA, 1996). Investigadora independiente CONICET desde 2007. Prof. Adj. Y directora del grupo de nanotecnomateriales del departamento de F&iacute;sica de la Facultad de Cs. Exactas y Naturales de la UBA desde 2003</p>\r\n<p><strong>- Aproximaciones para mejorar la seguridad y sanidad alimentaria mediante aplicaciones de nanotecnolog&iacute;a&rdquo;.<br /></strong><strong>Dra. Pilar Bruera</strong><strong>&nbsp;</strong>&ndash; Dra. En Ciencias Qu&iacute;micas. FCEyN. UBA (1987). Investigadora principal de CONICET. Profesora Titular, Depto. Qu&iacute;mica Org&aacute;nica, FCEyN, UBA. Departamento de Industrias y de Qu&iacute;mica Org&aacute;nica, FCEyN , UBA.<br /><strong>Coordina: Melina Bianchi</strong>&nbsp;de AATA (Asociaci&oacute;n Argentina de Tecn&oacute;logos Alimentarios)</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<h2>Torneo de F&uacute;tbol FIAR 2013</h2>\r\n<p>Este torneo, exclusivo para empresa, permite que cada firma que participe de&nbsp;<strong>FIAR</strong>&nbsp;pueda formar un equipo para competir por distintos premios. Aporta, adem&aacute;s, un ambiente de entretenimiento com&uacute;n para todos los compa&ntilde;eros de trabajo, desde la cumbre hasta los operantes, logrando una mejora en las relaciones humanas internas.</p>\r\n<p>El objetivo es brindar un espacio de encuentro deportivo para aquellos que tienen ganas de jugar y pasar una buena estad&iacute;a con f&uacute;tbol, premios, regalos y comida.</p>\r\n<p>La inscripci&oacute;n se realizar&aacute; a partir del mes de marzo.</p>\r\n<p>El torneo se desarrollar&aacute; en las instalaciones de Siberia Futbol, predio de la ciudad compuesto por 3 chanchas cubiertas, con c&eacute;sped sint&eacute;tico y todos los servicios necesarios para la realizaci&oacute;n de un evento de esta importancia.</p>\r\n<p>Se planific&oacute; que el mismo comience el d&iacute;a 23 de marzo, y se jugar&aacute;n los partidos todos los fines de semana, excepto los que sean largos o el que se encuentra afectado a la exposici&oacute;n de la Feria, hasta completar las 17 fechas, en el sistema de todos contra todos. De los 18 equipos representantes de empresas participantes, el ganador, segundo y tercer puesto ser&aacute;n premiados. Tambi&eacute;n se har&aacute;n menciones especiales al goleador del torneo, a la mejor hinchada, bloopers y fair play. Para m&aacute;s informaci&oacute;n.<a href=\"http://www.siberiafutbol.com.ar/\" target=\"_blank\"><strong>www.siberiafutbol.com.ar</strong></a>&nbsp;.</p>\r\n<p>&nbsp;</p>\r\n<h2>Rosario Chef 2013</h2>\r\n<p>La&nbsp;<strong>Asociaci&oacute;n Empresaria Hotelero Gastron&oacute;mica de Rosario (AEHGAR)</strong>, su escuela,&nbsp;<strong>Rosario Cocina Ideas</strong>y la&nbsp;<strong>Federaci&oacute;n Empresaria Hotelera Gastron&oacute;mica de la Rep&uacute;blica Argentina (F.E.H.G.R.A)</strong>&nbsp;presentan la ronda clasificatoria 2013 del<strong>&nbsp;</strong><strong>Torneo Nacional de Chefs: Buscando el Men&uacute; Argentino</strong>.</p>\r\n<p>El torneo tendr&aacute; lugar en el marco de la 8&ordm; edici&oacute;n de la&nbsp;<strong>FIAR - Feria Internacional de la Alimentaci&oacute;n Rosario -</strong>, los d&iacute;as&nbsp;<strong>8, 9, 10 y 11 de Mayo de 2013</strong>, en el Centro de Eventos Metropolitano (Av. Int. Lamas 610 - Alto Rosario Shopping) Rosario &ndash; Santa Fe.</p>\r\n<p>Dicho evento integra las rondas clasificatorias regionales para seleccionar al equipo que representar&aacute; a Rosario en el mencionado torneo nacional que se desarrollar&aacute; en el marco de&nbsp;<strong>Hotelga 2013</strong>&nbsp;(del<strong>&nbsp;</strong><strong>2 al 5 de septiembre de 2013</strong>), la mayor exposici&oacute;n hotelera gastron&oacute;mica del pa&iacute;s la cual propone un exclusivo y verdadero espacio representativo de la actividad.</p>\r\n<p>Todos aquellos que obtienen distinciones a nivel nacional son ampliamente reconocidos no s&oacute;lo por sus pares, sino tambi&eacute;n por embajadas extranjeras, hoteles y restaurantes de nuestro pa&iacute;s y el exterior, donde la cocina argentina contempor&aacute;nea ha comenzado a ganarse, hace varios a&ntilde;os, un lugar por derecho propio.</p>\r\n<p>Los interesados pueden consultar bases y condiciones en la escuela AEHGAR ingresando a<a href=\"http://www.rosariochefs.com.ar/\" target=\"_blank\"><strong>http://www.rosariochefs.com.ar</strong></a>.</p>\r\n<p>Inscripci&oacute;n sin cargo para los asociados AEHGAR.</p>\r\n<p>&nbsp;</p>', '0', '0', '0', '');
INSERT INTO `productos` VALUES ('106', '15', '1368274468-15', 'Workshop', 'Torneo Nacional de Chefs 2013', 'Torneo Nacional de Chefs, “Buscando el Menú Argentino”', 'Centro de Eventos Metropolitano', 'Av. Int. Lamas 610', '2000', '64651', '80', 'Argentina', '1367982000', '1368327599', 'http://www.rosariochefs.com.ar/', '', 'https://www.facebook.com/rosario.chefs', 'escuela@aehgar.com.ar', '', '0341 - 568-1166 / 67', '', 'torneo-nacional-de-chefs-2013', '1', NULL, '<p><span>La</span><strong>&nbsp;Asociaci&oacute;n Empresaria Hotelero Gastron&oacute;mica de Rosario, su Escuela, Rosario Cocina Ideas</strong><span>&nbsp;junto a</span><strong>F.E.H.G.R.A</strong><span>&nbsp;presentan la Ronda Clasificatoria 2013 del&nbsp;</span><strong>Torneo Nacional de Chefs, \"Buscando el Menu Argentino\".</strong></p>\r\n<p class=\"style1\">El Torneo tendr&aacute; lugar en el marco de la<strong>&nbsp;8&ordm; edici&oacute;n de la FIAR</strong>&nbsp;- Feria Internacional de la Alimentaci&oacute;n Rosario -, los d&iacute;as<strong>&nbsp;8, 9, 10 y 11 de Mayo de 2013</strong>, en el Centro de Eventos Metropolitano (Av. Int. Lamas 610 - Alto Rosario Shopping) Rosario &ndash; Santa Fe.</p>\r\n<p class=\"style1\">Dicho evento integra las Rondas Clasificatorias Regionales para seleccionar al equipo que representar&aacute; a Rosario en el mencionado Torneo Nacional que se desarrollar&aacute; en el marco de&nbsp;<strong>Hotelga 2013</strong>&nbsp;(del 2 al 5 de septiembre de 2013), la mayor exposici&oacute;n hotelera gastron&oacute;mica del pa&iacute;s la cual propone un exclusivo y verdadero espacio representativo de la actividad.</p>\r\n<p class=\"style1\">Cabe destacar que todos aquellos que obtienen distinciones a nivel nacional son ampliamente reconocidos no s&oacute;lo por sus pares, sino tambi&eacute;n por embajadas extranjeras, hoteles y restaurantes de nuestro pa&iacute;s y el exterior, donde la cocina argentina contempor&aacute;nea ha comenzado a ganarse, hace varios a&ntilde;os, un lugar por derecho propio.</p>\r\n<p class=\"style2\">&nbsp;</p>', '<table style=\"width: 695px; height: 1506px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"600\">\r\n<table style=\"width: 700px;\" border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<h2 class=\"style8\" align=\"center\">Lugar y fecha</h2>\r\n<table style=\"width: 100%;\" border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"60%\"><span class=\"style4\" data-mce-mark=\"1\">El Torneo Gastron&oacute;mico &ldquo;Rosario Chefs 2013&rdquo;, ronda clasificatoria del Torneo Nacional de Chefs &ldquo; Buscando el Men&uacute; Argentino&rdquo;, se realizar&aacute; en el marco de la FIAR &ndash; Feria Internacional de la Alimentaci&oacute;n Rosario - los d&iacute;as 8, 9, 10 y 11 de Mayo de 2013, en el Sal&oacute;n Metropolitano &ndash; Alto Rosario.</span></td>\r\n<td width=\"40%\">\r\n<div align=\"center\">&nbsp;</div>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3 class=\"mypets\">De los participantes</h3>\r\n<div class=\"thepet\">\r\n<p class=\"style4\"><strong>Art. 1 -</strong>&nbsp;Podr&aacute;n participar todos aquellos chefs, cocineros y pasteleros que se encuentren trabajando actualmente en establecimientos gastron&oacute;micos (ya sean restaurantes, restaurantes de hoteles, empresas de catering o casa de comidas) asociados a FEHGRA y/o AHT a trav&eacute;s de sus respectivas Filiales.</p>\r\n<p class=\"style4\"><strong>Art. 1b -</strong>&nbsp;Aquellos establecimientos no pertenecientes a las entidades mencionadas en el punto anterior que deseen participar de este Torneo, podr&aacute;n hacerlo luego de abonar el canon de participaci&oacute;n correspondiente, que les ser&aacute; informado en el momento de la inscripci&oacute;n.</p>\r\n<p class=\"style4\"><strong>Art. 2 -</strong>&nbsp;Al momento de la inscripci&oacute;n, los cocineros deber&aacute;n ser mayores de 18 a&ntilde;os. No se permite la participaci&oacute;n de menores de edad.</p>\r\n<p class=\"style4\"><strong>Art. 3 -</strong>&nbsp;Cada establecimiento participante deber&aacute; completar la Solicitud de Inscripci&oacute;n (una por cada participante del equipo) donde deber&aacute; constar: - Nombre y Apellido completo - Tipo y n&uacute;mero de Documento - Fecha de Nacimiento - Libreta Sanitaria (adjuntar fotocopia) - Tel&eacute;fono para contacto - Domicilio - Email para contacto - Datos completos del establecimiento donde trabaja y el cargo que ocupa en el mismo</p>\r\n<p class=\"style4\"><strong>Art. 4 -</strong>&nbsp;En caso de omisi&oacute;n de cualquiera de estos datos, el cocinero no podr&aacute; participar del certamen.</p>\r\n<p class=\"style4\"><strong>Art. 5 -</strong>&nbsp;En el caso de falsear alg&uacute;n dato o inscribir participantes que no se encuentren trabajando actualmente en el correspondiente establecimiento, ser&aacute; motivo suficiente para la descalificaci&oacute;n de todos los participantes pertenecientes a ese establecimiento.</p>\r\n</div>\r\n<h3 class=\"mypets\">Del equipo de trabajo</h3>\r\n<div class=\"thepet\">\r\n<p class=\"style4\"><strong>Art. 6 -&nbsp;</strong>Cada establecimiento podr&aacute; presentar hasta un (1) equipo para cubrir el presente Torneo. Por lo tanto, el equipo que representar&aacute; al establecimiento estar&aacute; formado por 1 (un) cocinero, 1 (un) pastelero y (1) ayudante. Los ayudantes pueden cumplir cualquier tarea, vale decir, podr&aacute;n ser cocineros, pasteleros, panaderos o ayudantes.</p>\r\n<p class=\"style4\"><strong>Art. 6.1 -</strong>&nbsp;Cada establecimiento podr&aacute; presentar hasta un cocinero suplente.</p>\r\n<p class=\"style4\"><strong>Art. 6.2 -</strong>&nbsp;El cocinero suplente podr&aacute; reemplazar a un participante titular toda vez que se determine su reemplazo en caso de accidente (cortes, quemaduras, etc.). Una vez reemplazado dicho titular, no podr&aacute; volver a integrar el equipo mientras dure la competencia.</p>\r\n<p class=\"style4\"><strong>Art. 7 -&nbsp;</strong>Los equipos estar&aacute;n conformados de la siguiente manera: 1 (un) Cocinero 1 (un) Pastelero 1 (uno) Ayudante</p>\r\n<p class=\"style4\"><strong>Art. 8 -&nbsp;</strong>Los cocineros participantes deber&aacute;n presentarse correctamente vestidos, con una correcta higiene de manos, u&ntilde;as (cortas y sin esmalte) y pelo. Al momento de participar, no deber&aacute;n portar reloj, anillos, pulseras u otro elemento similar en sus manos. En caso de poseer piercing en el rostro, estos deber&aacute;n retirarse o taparse con tela adhesiva durante el desarrollo de su labor. El uniforme constar&aacute; de:&nbsp;<br />a. Chaqueta blanca<br />b. Delantal blanco&nbsp;<br />c. Gorro blanco &oacute; gorra<br />d. Pantal&oacute;n negro o cuadrill&eacute;&nbsp;<br />e. Repasador&nbsp;<br />f. Zuecos &oacute; zapatos cerrados (blancos o negros)</p>\r\n<p class=\"style4\">Ya que una de las premisas del Torneo es la higiene personal, no presentarse en forma adecuada al certamen ser&aacute; motivo de descalificaci&oacute;n del participante.&nbsp;</p>\r\n</div>\r\n<h3 class=\"mypets\">De la disciplina</h3>\r\n<div class=\"thepet\">\r\n<p class=\"style4\">La disciplina elegida para este certamen es &ldquo;Men&uacute; de dos pasos&rdquo; para las Rondas Clasificatorias, y &ldquo;Men&uacute; de cinco pasos&rdquo; para la Gran Final Hotelga. Los detalles de la misma pueden verse en el Anexo 1.</p>\r\n<p class=\"style4\"><strong>Art. 9 -&nbsp;</strong>Para evaluar la tarea realizada, y emitir la puntuaci&oacute;n correspondiente, el Jurado tendr&aacute; en cuenta los siguientes valores de evaluaci&oacute;n sobre un total de 150 puntos:</p>\r\n<table style=\"width: 350px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p>&nbsp;</p>\r\n</td>\r\n<td class=\"style4\">\r\n<p>ITEMS A EVALUAR</p>\r\n</td>\r\n<td class=\"style4\">\r\n<p>PUNTAJE</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"11\">\r\n<p>&nbsp;</p>\r\n</td>\r\n<td class=\"style4\" valign=\"top\" width=\"265\">&middot;Mise en Place<br />&middot;Orden y limpieza<br />&middot;T&eacute;cnica<br />&middot;Puntos de Cocci&oacute;n<br />&middot;Temperatura del plato presentado<br />&middot; Presentaci&oacute;n y uniformidad de los platos<br />&middot;Equilibrio y armon&iacute;a nutricional<br />&middot;Sabor<br />&middot;Creatividad<br />&middot; Impacto visual</td>\r\n<td class=\"style4\" valign=\"top\" width=\"100\">10 puntos<br />20 puntos<br />10 puntos<br />20 puntos<br />20 puntos<br />10 puntos<br />10 puntos<br />20 puntos<br />15 puntos<strong><br /></strong>15 puntos\r\n<p>&nbsp;</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<h3 class=\"mypets\">De las materias primas</h3>\r\n<div class=\"thepet\">\r\n<p class=\"style4\"><strong>Art. 10 &ndash;&nbsp;</strong>No se podr&aacute;n utilizar materias primas con ning&uacute;n tipo de procedimiento previo, salvo los fondos que si podr&aacute;n llevarse realizados previamente.</p>\r\n<p class=\"style4\"><strong>Art. 11 &ndash;&nbsp;</strong>Los participantes podr&aacute;n utilizar las herramientas y utensilios que deseen sin restricciones.</p>\r\n<p class=\"style4\"><strong>Art. 12</strong>&nbsp;&ndash; Junto con la receta del trabajo a elaborar, el participante deber&aacute; presentar un listado con la correcta descripci&oacute;n de ingredientes y procedimientos a llevar a cabo en el desarrollo de su trabajo.</p>\r\n</div>\r\n<h3 class=\"mypets\">Del jurado</h3>\r\n<div class=\"thepet\">\r\n<p class=\"style4\"><strong>Art. 13 -&nbsp;</strong>El Jurado estar&aacute; compuesto por al menos 3 (tres) personalidades reconocidas de la gastronom&iacute;a profesional local y/o internacional, por cada uno de los d&iacute;as de competencia.</p>\r\n<p class=\"style4\"><strong>Art. 14 -</strong>&nbsp;Uno de Jurados elegidos ser&aacute; nombrado Presidente.</p>\r\n<p class=\"style4\"><strong>Art. 15 -&nbsp;</strong>Las decisiones de los Jurados ser&aacute;n inapelables.</p>\r\n</div>\r\n<h3 class=\"mypets\">Anexo 1</h3>\r\n<div class=\"thepet\">\r\n<p class=\"style8\">Disciplina para Rondas Clasificatorias</p>\r\n<p class=\"style9\">Men&uacute; de tres pasos</p>\r\n<p class=\"style4\"><strong>Tapa o entrada:&nbsp;</strong>Tapa o entrada: Ovo l&aacute;cteo vegetariana. No deber&aacute; pesar m&aacute;s de 150 gramos (total).</p>\r\n<p class=\"style4\"><strong>Plato Principal:&nbsp;</strong>elaborado con materias primas de la zona o que sean representativas del lugar. No deber&aacute; pesar m&aacute;s de 250 gramos (total).</p>\r\n<p class=\"style4\"><strong>Salsas y guarniciones:&nbsp;</strong>libres, a elecci&oacute;n de los participantes.</p>\r\n<p class=\"style4\"><strong>Postre:&nbsp;</strong>elaborado con 2 dulces t&iacute;picos nacionales, salsas y/o guarniciones a elecci&oacute;n.</p>\r\n<p class=\"style4\"><strong>Presentaci&oacute;n:&nbsp;</strong>tres (3) tapas o entradas (iguales), tres (3) platos principales (iguales) y tres (3) postres iguales.</p>\r\n<p class=\"style4\"><strong>Tiempo total de trabajo:&nbsp;</strong>2 horas.</p>\r\n<p class=\"style4\"><strong>Orden de participaci&oacute;n:&nbsp;</strong>se realizara por sorteo el mismo d&iacute;a del evento antes del comienzo del mismo, los equipos ingresaran de forma escalonada con 30 minutos de diferencia entre uno y otro.</p>\r\n</div>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', NULL, '<p>Los interesados pueden consultar bases y condiciones en la Escuela AEHGAR donde se les brindar&aacute; toda la informaci&oacute;n necesaria: escuela@aehgar.com.ar; rosario@fehgra.org.ar / Tel.: 5681166/67 - 4472144.<strong>Inscripci&oacute;n SIN CARGO para asociados AEHGAR</strong>. Para mayor informaci&oacute;n sobre HOTELGA 2013: www.hotelga.com.ar.</p>', '', 'Torneo Nacional de Chefs, “Buscando el Menú Argentino”', '447', '0', '1368274468', '0', NULL, '-32.92907672753086', '-60.66821757718503', '0', '0', '1367418986', '{\"organiza\":\"Organizaci\\u00f3n\",\"auspicios\":\"Auspicios\",\"autoridades\":\"Autoridades\",\"areas\":\"Bases\",\"trabajos\":\"Pres.Trabajos\",\"disertantes\":\"Expositores\",\"cronograma\":\"Actividades\",\"sponsors\":\"Sponsors\"}', NULL, NULL, '', '<p><span class=\"style1\">Con el objetivo de enriquecer las Rondas Clasificatorias se plantean las siguientes propuestas formativas:</span></p>\r\n<div>\r\n<ul>\r\n<li><span class=\"style3\">Presentaci&oacute;n del torneo</span><span class=\"style1\">&nbsp;el d&iacute;a Mi&eacute;rcoles 19 de diciembre de 2012 a las 18 hs, con la presencia de la Sra. Intendenta de Rosario; Dra. M&oacute;nica Fein; junto a otras autoridades.</span></li>\r\n<li><span class=\"style3\">Seminario taller: &ldquo;Cocina para Torneos&rdquo;.</span><span class=\"style1\">&nbsp;El taller tiene lugar el d&iacute;a 15 DE ABRIL de 16 a 20 hs, en 1&ordm; de Mayo 1676. Disertante a cargo: Chef Manuel Ausejo, miembro activo de la Brigada Restaurante Argentina de FEHGRA.</span></li>\r\n<li><span class=\"style3\">Clases Magistrales</span><span class=\"style1\">&nbsp;a cargo de reconocidos especialistas. Degustaciones.</span></li>\r\n<li><span class=\"style3\">Sector de Expositores:&nbsp;</span><span class=\"style1\">muestra de las &uacute;ltimas tendencias y novedades en materia de equipamiento, productos y servicios.</span></li>\r\n</ul>\r\n</div>', '0', '0', '0', '');
INSERT INTO `productos` VALUES ('107', '17', '1368218027-17', 'Simposio', 'II Simposio de Filosofía Moderna', 'Segundo Simposio de Filosofía Moderna', 'Facultad de Humanidades y Artes', 'Entre Ríos 758', '2000', '64651', '80', 'Argentina', '1383534000', '1383793199', 'http://www.filosofiamoderna.unr.edu.ar/', '', '', 'filosofiamoderna@unr.edu.ar', 'filosofiamoderna@unr.edu.ar', '', '', 'ii-simposio-de-filosofia-moderna', '1', NULL, '<h3 class=\"center\"><strong style=\"font-size: 2em;\">II&ordm; Simposio de Filosof&iacute;a Moderna &nbsp;</strong></h3>\r\n<h1 class=\"center\"><strong>4, 5 y 6 de Noviembre de 2013<br /></strong></h1>\r\n<p class=\"full\" style=\"color: #000000;\">Organizado por el <span class=\"bold\">Centro de Estudios de Filosof&iacute;a Moderna</span> perteneciente a la&nbsp;<span class=\"bold\">Facultad de Humanidades y Artes</span> de la&nbsp;Universidad Nacional de Rosario.<br /><br />Director del Centro: Dr. Alberto Mario Damiani<br /><br />Integrantes:&nbsp;<span data-mce-mark=\"1\">Dr. Mart&iacute;n Arias-Albizu,&nbsp;</span><span data-mce-mark=\"1\">Lic. Victor A. Alfaro,&nbsp;</span><span data-mce-mark=\"1\">Dra. Ileana P. Beade,&nbsp;</span><span data-mce-mark=\"1\">Lic. Mar&iacute;a Julia Bertolio,&nbsp;</span><span data-mce-mark=\"1\">Dr. Rodrigo S. Braicovich,&nbsp;</span><span data-mce-mark=\"1\">Dr. Luciano C&oacute;rsico,&nbsp;</span><span data-mce-mark=\"1\">Dr. Alberto M. Damiani,&nbsp;</span><span data-mce-mark=\"1\">Yael Gaisiner,&nbsp;</span><span data-mce-mark=\"1\">Prof. Maril&iacute;n Gomez,&nbsp;</span><span data-mce-mark=\"1\">Prof. Mar&iacute;a Victoria Gonz&aacute;lez.</span></p>\r\n<p class=\"left\" style=\"color: #000000;\">&nbsp;</p>', '<h2><span class=\"underline\">&Aacute;reas</span></h2>\r\n<h4 style=\"color: #000000;\"><span>Metaf&iacute;sica</span><br /><span>Teor&iacute;a del Conocimiento</span><br /><span>Filosof&iacute;a e Historia de las Ciencias</span><br /><span>Filosof&iacute;a del Lenguaje</span><br /><span>&Eacute;tica</span><br /><span>Filosof&iacute;a Pol&iacute;tica</span><br /><span>Antropolog&iacute;a Filos&oacute;fica Est&eacute;tica</span><br /><span>Filosof&iacute;a de la Historia</span><br /><span>L&oacute;gica</span><br /><span>Filosof&iacute;a de la Educaci&oacute;n</span><br /><span>Filosof&iacute;a del Derecho</span></h4>', NULL, '<h2>Inscripci&oacute;n (pago durante el evento):</h2>\r\n<h4>Expositores graduados: 200<br />Expositores alumnos: 100<br />Asistentes graduados: 50<br />Asistentes alumnos: sin cargo (Se otorgan certificados).</h4>', '<h2>Comunicaciones:</h2>\r\n<h4>Las mismas deber&aacute;n tener un m&aacute;ximo de 2500 palabras (fuente Times New Roman, hoja A4, interlineado 1&frac12;, m&aacute;rgenes predeterminados) y estar acompa&ntilde;adas de un resumen de no m&aacute;s de 300 palabras y un breve &nbsp;<span class=\"italic\">curriculum vitae&nbsp;</span>(m&aacute;ximo 100 palabras).</h4>\r\n<h4>Los res&uacute;menes y trabajos completos deber&aacute;n enviarse hasta el d&iacute;a 31 de agosto de 2013 a:&nbsp;</h4>\r\n<h4>filosofiamoderna@unr.edu.ar</h4>\r\n<h3>Est&aacute; prevista la publicaci&oacute;n de las actas en formato digital.</h3>', 'Simposio de Filosofía Rosario, Filosofía Moderna,  congreso de filosofía Rosario, jornada de filosofía Rosario, metafísica, teoría del conocimiento', '1588', '0', '1368218027', '0', NULL, '-32.9453648', '-60.64087770000003', '0', '0', '1367582478', '{\"organiza\":\"Organizaci\\u00f3n\",\"auspicios\":\"Auspicios\",\"autoridades\":\"Autoridades\",\"areas\":\"\\u00c1reas Tem\\u00e1ticas\",\"trabajos\":\"Pres.Trabajos\",\"disertantes\":\"Expositores\",\"cronograma\":\"Cronograma\",\"sponsors\":\"Sponsors\"}', NULL, NULL, '', '', '0', '0', '0', '');
INSERT INTO `productos` VALUES ('109', '19', '1368485346-19', 'Congreso', 'XVI Congreso Argentino AANEP 2013', 'XVI Congreso Argentino  - VIII del Cono Sur de Soporte Nutricional y Metabolismo y IV Congreso Argentino de Soporte Nutricional y Metabolismo en Pediatría', 'Ros Tower - Hotel and Convention Center', 'Mitre 299', '2000', '64651', '80', 'Argentina', '1382497200', '1382756399', 'http://www.aanep.org.ar/congresos_aanep.php', '', '', 'aanep@fibertel.com.ar', '', '54-11-48642804', '', 'xvi-congreso-argentino-aanep-2013', '1', NULL, '<p><span class=\"bold\">Palabras el Presidente:</span></p>\r\n<p>La salud y la enfermedad requieren de una mirada desde la perspectiva de la complejidad. Esto es, que a partir de abordajes interdisciplinarios, superando las tensiones, rupturas y contradicciones que representan las diferentes disciplinas, sea posible construir desde las divergencias planteadas en los planos conceptuales o metodol&oacute;gicos y as&iacute; establecer contribuciones espec&iacute;ficas.</p>\r\n<p><br />Es fundamental por tanto, trabajar en la construcci&oacute;n de nuevos modelos de atenci&oacute;n, tendientes a crear conocimientos que mejoren la calidad de vida de los ciudadanos. En esta tarea esta la AANEP desde sus or&iacute;genes.</p>\r\n<p>Sumando juntos, pensando, que olvidar la importancia de practicar de manera permanente y perseverante la solidaridad y la fraternidad, lleva al malestar y la desarmon&iacute;a.</p>\r\n<p>La vida en armon&iacute;a requiere ejes de pensamiento sustentados en firmes y sinceros cimientos afectivos, donde el &ldquo;nosotros&rdquo; prevalezca sobre el &ldquo;yo&rdquo; sin que, por ello, cada uno encuentre cercenado el sendero que conduce a convertirse en &ldquo;&uacute;nico e irrepetible&rdquo;.</p>\r\n<p>Es evidente que cuando nos unimos para hacer un esfuerzo previamente consensuado, con un sentido claro y preciso, la energ&iacute;a que debe poner cada uno es menor y alcanzar la meta es un proceso sereno.</p>\r\n<p><br />Es prioritario dar herramientas que intenten revertir el desconocimiento, una de ellas, tal vez la m&aacute;s importante es la educaci&oacute;n, rol fundamental de las sociedades cient&iacute;ficas.</p>\r\n<p>Educaci&oacute;n para generar trabajo con responsabilidad y calidad. Educar para conocer, conocer para transformar. Transformar la realidad de los que sufren el padecimiento y sus consecuencias.</p>\r\n<blockquote>\r\n<p><span data-mce-mark=\"1\">Saber que se puede, querer que se pueda. Aprendamos para:&hellip;.. saber que se puede, y unamos nuestras voluntades para:&hellip;.. querer que se pueda.</span></p>\r\n</blockquote>\r\n<p><span class=\"underline\"><span class=\"bold\">Amigos</span></span>: queremos invitarlos al &ldquo;<span class=\"bold\">XVI CONGRESO ARGENTINO VIII DEL CONO SUR DE SOPORTE NUTRICIONAL Y METABOLISMOy al IV CONGRESO ARGENTINO DE SOPORTE NUTRICIONAL Y METABOLISMO EN PEDIATRIA</span> &rdquo;, a realizarse el 23, 24 Y 25 de OCTUBRE de 2013 en el Hotel Ros Tower - Hotel &amp; Convention Center en la ciudad de ROSARIO, SANTA FE, ARGENTINA, esperando que sea un buen sitio de reuni&oacute;n, de intercambio, de aprendizaje y sobre todo de uni&oacute;n para que, con los mismos objetivos, construyamos un camino juntos.</p>\r\n<p class=\"right\"><br /><span class=\"bold\">Dr. Humberto E. Fain</span><br /><span class=\"bold\">Presidente Congreso Argentino</span><br /><span class=\"bold\">Rosario. Santa Fe. Argentina</span></p>\r\n<h2>&nbsp;</h2>', '<h2>Ejes Tem&aacute;ticos:</h2>\r\n<ul>\r\n<li>Filiales Activas - Participaci&oacute;n de las filiales con desarrollo de un eje tem&aacute;tico de forma interdisciplinaria.</li>\r\n<li>Incumbencias y Responsabilidades.</li>\r\n<li>Soporte Nutricional y Comunidad - Talleres dirigidos a la comunidad en conjunto con las organizaciones de familias.</li>\r\n</ul>\r\n<h2>&nbsp;TEMARIO</h2>\r\n<ul>\r\n<li>&nbsp;Desnutrici&oacute;n Hospitalaria. Hemos hecho algo?</li>\r\n<li>Pacientes cr&iacute;ticos. S&oacute;lo cantidad de nutrientes?</li>\r\n<li>Terapia nutricional perioperatoria hoy. Reflexiones y Controversias.</li>\r\n<li>Situaciones de abordaje conjunto.&nbsp;pancreatitis, f&iacute;stulas , EII.</li>\r\n<li>Desnutrici&oacute;n secundaria. Cancer. Insuficiencia renal y hep&aacute;tica. Geriatr&iacute;a</li>\r\n<li>Obesidad: cuando la dieta no basta. Cirug&iacute;a de la obesidad.</li>\r\n<li>Insuficiencia Intestinal. Que mas podemos hacer para la adaptaci&oacute;n. Cirug&iacute;a</li>\r\n<li>Inmunonutrici&oacute;n en la pr&aacute;ctica diaria</li>\r\n<li>El paciente neurol&oacute;gico. Desde el trauma agudo a la secuela.</li>\r\n<li>Disparidad del crecimiento de ni&ntilde;os . Entre la desnutrici&oacute;n y la obesidad</li>\r\n<li>Soporte Nutricional Domiciliario. Distintos efectores, distinta pr&aacute;ctica?</li>\r\n<li>Enfermedades hereditarias. Que hacemos en la transici&oacute;n del ni&ntilde;o al adulto?</li>\r\n<li>Cuando el nutriente es la forma de tratamiento: ECM, dieta cetog&eacute;nica y otras</li>\r\n<li>Cuando el nutriente es la noxa: alergia alimentaria.</li>\r\n<li>Valoraci&oacute;n del estado nutricional: relaci&oacute;n antropometr&iacute;a-funci&oacute;n y pron&oacute;stico</li>\r\n<li>Nutrici&oacute;n Enteral. Optimizaci&oacute;n. Que hay de nuevo y bueno.</li>\r\n<li>Nutrici&oacute;n Parenteral. Ingredientes y procesos. Seguridad. L&iacute;pidos: cantidad y calidad.</li>\r\n<li>Micronutrientes. Aportamos lo que calculamos?</li>\r\n<li>AbordajeNutricional en el Neonato. Oportunidades y consideraciones.</li>\r\n<li>Etica en el Soporte Nutricional.</li>\r\n</ul>', NULL, '<h2>PREINSCRIPCION Y FORMA DE PAGO</h2>\r\n<h3>En la sede de la Aanep:</h3>\r\n<p>Lavalle 3643 3&ordm; &ldquo;F&rdquo;- Buenos Aires- Tel.: 54-11-4864-2804</p>\r\n<h3><br />Por dep&oacute;sito bancario:</h3>\r\n<p>Enviar por fax a la AANEP, el tal&oacute;n de dep&oacute;sito junto con la ficha de inscripci&oacute;n. Presentar el tal&oacute;n de dep&oacute;sito al inicio<br />de las jornadas en la secretaria del congreso - Banco de Galicia. Cuenta Corriente N&ordm; 252-5303-4 A nombre de la Asociaci&oacute;n Argentina de&nbsp;Nutrici&oacute;n Enteral y Parenteral.</p>\r\n<h3><br />Por transferencia bancaria:</h3>\r\n<p>Enviar por fax a la AANEP, el tal&oacute;n de dep&oacute;sito junto con la ficha de inscripci&oacute;n. Presentar el tal&oacute;n de dep&oacute;sito al <br />inicio de las jornadas en la secretaria del congreso - Banco de Galicia CBU 0070303920000000252542 - A nombre de la Asociaci&oacute;n Argentina de Nutrici&oacute;n Enteral y Parenteral.</p>\r\n<h3><br />Pago por medio de cheques:</h3>\r\n<p>En cheque libre de gastos, a la orden de: ASOCIACION ARGENTINA DE NUTRICION ENTERAL Y PARENTERAL &ldquo;NO <br />A LA ORDEN&rdquo; .Enviar por correo privado a A.A.N.E.P.- Lavalle 3643, 3&ordm; F, (1190) Capital Federal. El comit&eacute; organizador no admitir&aacute; responsabilidad alguna por cualquier p&eacute;rdida de dinero por env&iacute;os que se efect&uacute;en por correo com&uacute;n.</p>\r\n<p><br /><span class=\"bold\">NOTA</span>: Una vez acreditado el pago, se remitir&aacute; el correspondiente recibo.<br />Inscripci&oacute;n en el congreso: Solo en pesos / d&oacute;lares y en efectivo</p>', '', 'XVI Congreso Argentino  - VIII del Cono Sur de Soporte Nutricional y Metabolismo y IV Congreso Argentino de Soporte Nutricional y Metabolismo en Pedia', '3196', '0', '1368485346', '0', NULL, '-32.940462', '-60.638111200000026', '0', '0', '1368481663', '{\"organiza\":\"Organizaci\\u00f3n\",\"auspicios\":\"Auspicios\",\"autoridades\":\"Comit\\u00e9 Cient\\u00edfico\",\"areas\":\"\\u00c1reas Tem\\u00e1ticas\",\"trabajos\":\"Pres.Trabajos\",\"disertantes\":\"Expositores\",\"cronograma\":\"Cronograma\",\"sponsors\":\"Sponsors\"}', NULL, NULL, '', '', '0', '1376621999', '1382583599', '');
INSERT INTO `productos` VALUES ('110', '20', '1368816819-20', 'Jornada', 'Prevención y detección del cáncer Bucal', 'Campaña de prevención y detección del cáncer bucal\r\n', 'Facultad de Odontología UNR', 'Santa Fe 3160 Rosario.', '2000', '64651', '80', 'Argentina', '1369450800', '1387249199', 'http://www.fodonto.unr.edu.ar/index.asp', '', 'https://www.facebook.com/FodontoUNR', '', '', '0341 - 4804606/07 ', '', 'prevencion-y-deteccion-del-cancer-bucal', '1', NULL, '<h2>Organizadas por:</h2>\r\n<ul>\r\n<li>Facultad de Odontolog&iacute;a - UNR</li>\r\n<li>Facultad de Ciencias M&eacute;dicas - UNR</li>\r\n<li>Liga Argentina de Lucha contra el c&aacute;ncer - LALCEC</li>\r\n<li>Centro de apoyo y rehabilitaci&oacute;n para operados de laringe - CAROL</li>\r\n</ul>', '', NULL, '', '', '', '914', '0', '1368816819', '0', NULL, '-32.9399749', '-60.66617259999998', '0', '0', '1368753021', '{\"organiza\":\"Organizaci\\u00f3n\",\"auspicios\":\"Informaci\\u00f3n\",\"autoridades\":\"Autoridades\",\"areas\":\"\\u00c1reas Tem\\u00e1ticas\",\"trabajos\":\"Pres.Trabajos\",\"disertantes\":\"Expositores\",\"cronograma\":\"Cronograma\",\"sponsors\":\"Auspiciado por\"}', NULL, NULL, '<h2>Ante la presencia por m&aacute;s de 15 d&iacute;as de:</h2>\r\n<ul>\r\n<li>&Uacute;lceras o llagas, manchas rojas o blancas.</li>\r\n<li>Bultos, durezas o sangrado.</li>\r\n<li>Molestias o dificultades para hablar, masticar y/o tragar.</li>\r\n<li>Ardor, hormigueo o adormecimiento.</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<blockquote>\r\n<p>Realice en forma GRATUITA un EXAMEN BUCAL PREVENTIVO</p>\r\n<p>de lesiones precursoras del C&aacute;ncer Bucal</p>\r\n</blockquote>\r\n<p>&nbsp;</p>\r\n<h2>&iquest;D&oacute;nde?</h2>\r\n<h3>Facultad de Odontolog&iacute;a / UNR - C&aacute;tedra de Estomatolog&iacute;a I y II</h3>\r\n<ul>\r\n<li>Santa Fe 3160 7mo. piso</li>\r\n<li>Martes y Jueves</li>\r\n<li>De 09:00 a 11:00hs.</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<h3>Facultad de Ciencias M&eacute;dicas / UNR - C&aacute;tedra de 