INSERT INTO `productos` VALUES ('145', '18', '1427750870-18', 'Congreso', '1er. CONGRESO DE COMEDIA MUSICAL', '1er. CONGRESO DE COMEDIA MUSICAL\r\nEstudio de Comedias Musicales. Teatro El Cículo', 'Teatro el Círculo de Rosario', 'Mendoza 771', '', '64651', '80', 'Argentina', '1429326000', '1429585199', 'http://www.ecomediasmusicales.com.ar/', '', 'https://www.facebook.com/estudiodecomediasmusicales', 'congresoecm@gmail.com', 'congresoecm@gmail.com', '0341 5489868', '', '1er-congreso-de-comedia-musical', '1', NULL, '<p style=\"color: #000000;\">Como cierre de los primeros <span class=\"bold\">20 a&ntilde;os del Estudio de Comedias Musicales Teatro El Circulo</span>, y por iniciativa&nbsp;de&nbsp;sus primeros alumnos, hoy docentes y artistas reconocidos en el musical argentino y en el mundo. se realiz&aacute; el&nbsp;<span class=\"bold\">1er.CONGRESO DE COMEDIA MUSICAL ECM. TEATRO EL CIRCULO</span></p>\r\n<p style=\"color: #000000;\">&nbsp;</p>\r\n<p style=\"color: #000000;\">Cada uno de los docentes y disertantes que participar&aacute;n del Congreso se inici&oacute; en el Estudio de Comedias Musicales o fue docente de esta casa desde el a&ntilde;o de su fundaci&oacute;n.&nbsp;</p>\r\n<p style=\"color: #000000;\">Veinte a&ntilde;os despu&eacute;s, han recorrido&nbsp; su camino dentro del mundo del arte en&nbsp;sus diversas manifestaciones.<br />Ello se ve plasmado hoy en el &nbsp;variad&iacute;simo programa de un&nbsp;CONGRESO DE COMEDIA MUSICAL&nbsp;cuyo cuerpo docente est&aacute; formado &iacute;ntegramente por egresados, ex alumnos o docentes de la primera y segunda promoci&oacute;n de este&nbsp;Estudio de Comedias Musicales.</p>\r\n<p style=\"color: #000000;\">&nbsp;</p>\r\n<h3>DIRECTORA Y FUNDADORA ECM:</h3>\r\n<p><span class=\"bold\">Nora Gonz&aacute;lez Pozzi</span></p>\r\n<h3>DIRECCI&Oacute;N GENERAL Y ART&Iacute;STICA DEL CONGRESO:</h3>\r\n<p><span class=\"bold\">Luciana Gonz&aacute;lez Pozzi</span></p>\r\n<h3>RELACIONES INSTITUCIONALES:</h3>\r\n<p><span class=\"bold\">Sebasti&aacute;n Mazzoni</span></p>\r\n<h3>PRENSA Y DIFUSI&Oacute;N:</h3>\r\n<p><span class=\"bold\">Sof&iacute;a Monje</span></p>\r\n<h3>ADMINISTRACI&Oacute;N:</h3>\r\n<p><span class=\"bold\">Josefina Facciano</span></p>\r\n<h3>COORDINACION WEB:</h3>\r\n<p><span class=\"bold\">IUNYC Comunicaci&oacute;n</span></p>\r\n<h3>PRODUCCI&Oacute;N:</h3>\r\n<p><span class=\"bold\">ESTUDIO DE COMEDIAS MUSICALES.Mariana Gonz&aacute;lez Pozzi.</span></p>\r\n<h3>AGRADECIMIENTOS:</h3>\r\n<p><span class=\"bold\">Teatro El C&iacute;rculo.</span></p>', '<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img title=\"Evento de Cierre\" src=\"http://www.ecomediasmusicales.com.ar/congreso/img/evento-16.png\" alt=\"Evento de Cierre\" width=\"700\" height=\"757\" /></p>', NULL, '<p><img class=\"center\" src=\"http://www.ecomediasmusicales.com.ar/congreso/img/ENTRADA.png\" alt=\"\" width=\"700\" height=\"478\" /></p>\r\n<h2 class=\"center\">Se considerar&aacute;n inscriptos aquellos que acrediten el pago antes del 15 de Abril del 2015</h2>\r\n<p>&nbsp;</p>', '', 'I CONGRESO DE COMEDIA MUSICAL ECM. TEATRO EL CIRCULO', '1101', '0', '1427750870', '1', NULL, '-32.952261', '-60.63495590000002', '0', '0', '1425941874', '{\"organiza\":\"Staff\",\"auspicios\":\"Auspicios\",\"autoridades\":\"Autoridades\",\"areas\":\"Evento Final\",\"trabajos\":\"Pres.Trabajos\",\"disertantes\":\"Docentes\",\"cronograma\":\"Programa\",\"sponsors\":\"Sponsors\"}', NULL, NULL, '', '<h2>CHARLA DE INICIO Y CIERRE:</h2>\r\n<h3>Nora Gonzalez Pozzi</h3>\r\n<p><span class=\"bold\" data-mce-mark=\"1\">FUNDADORA Y DIRECTORA ESTUDIO DE COMEDIAS MUSICALES</span></p>\r\n<p>&nbsp;</p>\r\n<h2>ADULTOS - 16 a&ntilde;os en adelante</h2>\r\n<h3>&middot; INTEGRACION EN LA COMEDIA MUSICAL</h3>\r\n<p><span class=\"bold\" data-mce-mark=\"1\">Christian Gimenez</span></p>\r\n<h3>&middot; T&Eacute;CNICA VOCAL Y REPERTORIO DE COMEDIA MUSICAL BELTING AND VOICE MIX</h3>\r\n<p><span class=\"bold\" data-mce-mark=\"1\">Sebasti&aacute;n Mazzoni</span></p>\r\n<h3>&middot; TECNICA VOCAL REPERTORIO DE COMEDIA MUSICAL- VOZ DE CABEZA, CL&Aacute;SICO Y LEGGED</h3>\r\n<p><span class=\"bold\" data-mce-mark=\"1\">Romina Casella</span></p>\r\n<h3>&middot; TECNICAS DE INTERPRETACION EN EL CANTO APLICADAS AL REPERTORIO DE COMEDIA MUSICAL</h3>\r\n<p><span class=\"bold\" data-mce-mark=\"1\">Ver&oacute;nica Valazza</span></p>\r\n<h3>&middot; LOS PERSONAJES EN EL MUSICAL, SU COMPOSICI&Oacute;N (EL ACTOR CANTANTE)</h3>\r\n<p><span class=\"bold\" data-mce-mark=\"1\">C&eacute;sar Spinelli</span></p>\r\n<h3>&middot; INTEGRACION CORAL APLICADA AL REPERTORIO DE MUSICAL</h3>\r\n<p><span class=\"bold\" data-mce-mark=\"1\">Romina Negri &ndash; C&eacute;sar Spinelli</span></p>\r\n<h3>&middot; MONTAJE COREOGRAFICO</h3>\r\n<p><span class=\"bold\" data-mce-mark=\"1\">Alejandro Lavallen</span></p>\r\n<h3>&middot; &ldquo;LA MUSICA COMO EL DISPARADOR DEL MOVIMIENTO&rdquo; FOSEE COMO LA S&Iacute;NTESIS DE ESTE CONCEPTO</h3>\r\n<p><span class=\"bold\" data-mce-mark=\"1\">Graciela Ortiz</span></p>\r\n<h3>&middot; TAP</h3>\r\n<p><span class=\"bold\" data-mce-mark=\"1\">Graciela Ortiz</span></p>\r\n<h3>&middot; ENTRENAMIENTO PARA BAILARINES</h3>\r\n<p><span class=\"bold\" data-mce-mark=\"1\">Yael Rodriguez</span></p>\r\n<h3>&middot; MONTAJE DE PUESTA TEATRAL</h3>\r\n<p><span class=\"bold\" data-mce-mark=\"1\">Gonzalo Catalani</span></p>\r\n<h3>&middot; TEATRO DE TEXTO</h3>\r\n<p><span class=\"bold\" data-mce-mark=\"1\">Leo Bossio</span></p>\r\n<h3>&middot; TEATRO COMUNICACI&Oacute;N, EXPRESI&Oacute;N Y CRECIMIENTO. CONSTRUCCION DEL PERSONAJE</h3>\r\n<p><span class=\"bold\" data-mce-mark=\"1\">Bibiana Kiener De Ensinck</span></p>\r\n<h3>&middot; PREPARACI&Oacute;N DE CASTING Y AUDICIONES</h3>\r\n<p><span class=\"bold\" data-mce-mark=\"1\">Sebasti&aacute;n Mazzoni</span></p>\r\n<h3>&middot; ACTUACI&Oacute;N FRENTE A C&Aacute;MARA Y PREPARACION PARA CASTING EN TELEVISION</h3>\r\n<p><span class=\"bold\" data-mce-mark=\"1\">Carlos Durigoni</span></p>\r