INSERT INTO `productos_disertantes` VALUES ('36', '17', '107', '', 'Fernando Bahr (Universidad Nacional del Litoral)', 'Dr. en Filosofía e Investigador Adjunto del CONICET. Prof. Titular de Historia de la Filosofía Moderna de la UNL. Actualmente investiga: \"Los argumentos escépticos en la filosofía clandestina de la temprana Ilustración\". ', '1', '0', '', 'argentina.gif');
INSERT INTO `productos_disertantes` VALUES ('37', '17', '107', '', 'Daniel Brauer (Universidad de Buenos Aires)', 'Dr. en Filosofía e  Investigador Principal del CONICET. Prof. titular de Metafísica y Filosofía de la historia de la UBA. Su investigación en curso es: \"La reconstrucción del pasado y la pregunta por el sentido de la evolución histórica II\". ', '1', '1', '', 'argentina.gif');
INSERT INTO `productos_disertantes` VALUES ('38', '17', '107', '', 'Julio De Zan (Universidad Nacional de San Martín)', 'Dr. en Filosofía e Investigador Principal del CONICET. Prof. titular de Filosofía práctica de la UNSAM. Su investigación en curso trata sobre los escritos políticos del Joven Hegel. ', '1', '2', '', 'argentina.gif');
INSERT INTO `productos_disertantes` VALUES ('39', '17', '107', '', 'Oscar Esquisabel (Universidad Nacional de La Plata)', 'Dr. en Filosofía e Investigador Independiente del CONICET. Prof. titular de Metafísica de la UNLP. Su investigación actual se titula: \"Lógica y ontología en el pensamiento de Leibniz. Interconexiones entre la característica general y la metafísica\". ', '1', '3', '', 'argentina.gif');
INSERT INTO `productos_disertantes` VALUES ('40', '17', '107', '', 'Graciela Fernández (Universidad Nacional de Mar del Plata)', 'Dra. En Filosofía, Profesora Titular de Historia de la Filosofía Moderna en la UNMdP y Profesora del Doctorado en Filosofía de la UNLa. Ha sido investigadora independiente del CONICET. Actualmente investiga las filosofías trascendentales de Kant y Husserl. ', '1', '4', '', 'argentina.gif');
INSERT INTO `productos_disertantes` VALUES ('41', '17', '107', '', 'Claudia Jáuregui (Universidad de Buenos Aires)', 'Dra. en Filosofía e Investigadora Independiente del CONICET. Prof. Adjunta de Historia de la Filosofía Moderna de la UBA. Su investigación actual: \"Condiciones formales y condiciones materiales de posibilidad de la experiencia en la filosofía trascendental de Kant\". ', '1', '5', '', 'argentina.gif');
INSERT INTO `productos_disertantes` VALUES ('42', '17', '107', '', 'Susana Maidana (Universidad Nacional de Tucumán)', 'Dra. en Filosofía y Prof. Titular de Historia de la Filosofía Moderna en la UNT. Su investigación actual trata sobre la filosofía política de David Hume. ', '1', '6', '', 'argentina.gif');
INSERT INTO `productos_disertantes` VALUES ('43', '17', '107', '', 'Ricardo Maliandi (Universidad Nacional de Mar del Plata)', 'Dr. en Filosofía, Prof. titular de Ética en la Universidad Nacional de Mar del Plata. Académico de la Academia Nacional de Ciencias. Fue profesor titular de Ética en la UBA e investigador superior del CONICET. Actualmente trabaja en su teoría denominada \"Ética convergente\".', '1', '7', '', 'argentina.gif');
