INSERT INTO `productos_disertantes` VALUES ('53', '23', '112', '23-112-1370281045.jpg', 'Prof Dr. Luis Tello', '“ABDOMEN AGUDO. CASOS CLÍNICOS”\r\n“GATO EN ER...MUY DIFERENTE A LOS PERROS”\r\n“TRAUMA MÚLTIPLE, SALVANDO VIDAS”\r\n\r\nMédico Veterinario, M. Sc. Veterinarias, Clínica de Animales Pequeños, Director Programa de Especialización en Clínica de Animales Menores, Universidad de Chile. Formación de especialidad en Emergencias y Cardiologia en: College of Veterinary Medicine, University of Washington, USA College of Veterinary Medicine, University of Virginia Tech, USA College of Veterinary Medicine, University of Davis, USA. Ex -Vicepresidente World Small Animal Veterinary Association. Director de asuntos académicos Latin American Veterinary Emergency and Critical Care Society :LAVECCS.', '1', '0', '', 'chile.gif');
INSERT INTO `productos_disertantes` VALUES ('54', '23', '112', '', 'Prof. Dr. Juan Carlos Perfumo', '“ENFERMEDADES EMERGENTES DEL CERDO EN LA ARGENTINA, PASADO, PRESENTE Y FUTURO. EL EJEMPLO DE LA INFECCION POR EL VIRUS DE INFLUENZA A”\r\n\r\nEgresado de la Universidad Nacional de La Plata, Faculty of Agriculture,Tokyo University\r\nDocente en patología especial y clínica y sanidad porcina. Diagnóstico anatomopatológico de enfermedades de los cerdos.\r\nCuarenta años de docencia e investigación en el área de sanidad porcina. Experiencia en Argentina, Paraguay, Perú, Venezuela y Nicaragua.', '1', '0', '', 'argentina.gif');
INSERT INTO `productos_disertantes` VALUES ('55', '23', '112', '23-112-1370281117.jpg', 'Prof. MV., Ms Sc. Carlos Sorribas', '“CLÍNICA DEL APARATO REPRODUCTOR MASCULINO EN CANINOS”\r\n\r\nMédico Veterinario. Facultad de Ciencias Veterinarias. Universidad Nacional de Rosario. Licenciado en Ciencias de la Educación. Universidad Nacional de Quilmes.  Master en Sociología . Universidad de Lomas de Zamora. Academia de Ciencias de la República Checa. Instituto de Sociología. Ex profesor adjunto ordinario de Anatomía y Fisiología Animal Fac Cs Agrarias UNR. Profesor adjunto ordinario de Clínica de Animales de Compañía Fac. Cs. Veterinarias UNR.Autor de varios Libros de la Especialidad, Disertante sobre temas de la especialidad en Argentina, Uruguay, Paraguay, Brasil, México, Perú, El Salvador y España. Autor de más de 50 trabajos sobre temas de la especialidad publicados y presentados en Congresos. Autor de 10 libros de la especialidad.', '1', '0', '', 'argentina.gif');
INSERT INTO `productos_disertantes` VALUES ('56', '23', '112', '23-112-1370281156.jpg', 'MVZ. Eduardo Puente', '“DIAGNÓSTICO DIFERENCIAL EN LAS INTERRUPCIONES DE LA GESTACIÓN EN EL BOVINO”\r\n\r\n•Médico Veterinario Zootecnista egresado del la Universidad Nacional Autónoma de México.\r\n•Jefe de medicina preventiva en la cuenca lechera de Tizayuca Hidalgo Mex. Durante 6 años (24 mil vacas en 22 hectáreas).\r\n\r\n18 años en Pfizer:\r\n\r\n•Gerente técnico en ganadería para México. \r\n•Gerente técnico en ganadería para Latinoamérica Pfizer.\r\n•Actualmente Gerente de distrito en Argentina para Zoetis.\r\n\r\nArtículos publicados de Neosporosis y DVB', '1', '0', '', 'mexico.gif');
INSERT INTO `productos_disertantes` VALUES ('57', '23', '112', '23-112-1370281190.jpg', 'MV. Eial Izak', '“CONTROL DE MOSCAS Y MASTITIS”\r\n\r\nMédico Veterinario egresado de la Facultad de Ciencias Veterinarias de la Universidad de Buenos Aires. Consultor privado en Mastitis y Calidad de Leche. Miembro del National Mastitis Council y Aprocal. Disertante en Argentina, Uruguay, Brasil, Paraguay, Chile, Perú y México y publicado artículos en diferentes medios del sector lechero y en congresos nacionales e internacionales. Ha participado y disertado en congresos en Israel, México, Japón, EE.UU., Canadá, Holanda, Irlanda, Bélgica y Nueva Zelanda.\r\n', '1', '0', '', 'argentina.gif');
INSERT INTO `productos_disertantes` VALUES ('58', '23', '112', '23-112-1370281219.jpg', 'Prof. MV. MsCs. Ernesto Raúl Odriozola', '“ENFERMEDADES DEL BOVINO CON SIGNOS NERVIOSOS”\r\n\r\nMédico Veterinario ,Universidad Nacional de La Plata. Maestría en la University of Waikato. New Zealand. Master of Philosophy, Toxicología Veterinaria.\r\nDesde 1993 a la actualidad Coordinador del Diagnostico Veterinario Especializado del INTA Balcarce.\r\nResponsable del Laboratorio de Toxicología del INTA Balcarce.\r\nDocente de Grado y posgrado.\r\nMás de 80 publicaciones en Revistas nacionales e internacionales.', '1', '0', '', 'argentina.gif');
INSERT INTO `productos_disertantes` VALUES ('59', '23', '112', '23-112-1370281248.jpg', 'Prof. Dr. Roberto Mauricio Carvalho Guedes', '“PRINCIPALES ENFERMEDADES ENTÉRICAS EN CERDOS DE DESARROLLO Y TERMINACIÓN”\r\n“CASOS CLÍNICOS INTERESANTES”\r\n\r\nMedicina Veterinaria de la Universidad Federal de Minas Gerais(año 1992); Pos-grado: Maestría en Medicina Veterinaria /Patología Veterinaria de la Universidad Federal de Minas Gerais (año 1995) y Doctorado Patobiología Veterinaria orientado en Enfermedades Infecciosas, otorgado por la University of Minesota (año 2002); Pos Doctorado en el Laboratorio de Enteropatía Proliferativa en University of Minesote (año 2003). \r\nProfesor visitante en Danish Technical University, en Copenhagen(años 2008-2009). Actualmente es profesor Asociado de la Universidad Federal de Minas Gerais y Tesorero de la Asosación Brasilera de Veterinarios Especializados en Suínos(ABRAVES), regional de Minas Gerais. Además tiene experiencia en el area de Medicina Veterinaria, con énfasis en Anatomía Patológica Animal, actuando en temas cómo: Suínos y Enfermedades.', '1', '0', '', 'brasil.gif');
INSERT INTO `productos_disertantes` VALUES ('64', '28', '118', '', 'Dr. Ps. Balbi, Juan', 'Lic. en Psicología (Universidad de Buenos Aires)\r\nDoctor en Psicología (Universidad del Salvador)\r\nDirector de CETEPO (Centro de Terapia Cognitiva Posracionalista)\r\nPresidente de la Fundación FAPS (Fundación para el Avance de los Estudios Posracionalistas), que cofundó con Vittorio Guidano\r\nMiembro didacta de la Asociación Argentina de Terapia Cognitiva y de la Sociedad Italiana de Terapia Comportamental y Cognitiva\r\nDirector del Curso de posgrado “Entrenamiento Clínico y Teórico en Terapia Cognitiva Posracionalista” que se dicta en IUNIR (Universidad del Hospital Italiano de Rosario, Argentina)\r\nProfesor invitado en numerosas universidades de Argentina, otros países de Latinoamérica e Italia\r\nAutor del libro ”La mente narrativa. Hacia una concepción posracionalista de la identidad personal”, Buenos Aires. Paidós, 2004. Obra traducida y publicada  en italiano por:  Franco Angeli. Milano. 2009.\r\nAutor del libro “Terapia Cognitiva Posracionalista. Conversaciones con Vittorio Guidano”, Buenos Aires. Biblos, 1994 (nueva edición Psicolibro ediciones, Buenos Aires, 2011)', '1', '0', '', 'argentina.gif');
